css:
@-moz-document domain("youtube.com")
{

ytd-feed-filter-chip-bar-renderer { display: none; }
yt-formatted-string[aria-label*="like"] { display: none !important; }
yt-related-chip-cloud-renderer { display: none !important; }

#metadata-line span:nth-child(1) { display: none !important; }

#endpoint[title="Explore"] { display: none !important; }

#sections ytd-guide-section-renderer:nth-child(2),
#sections ytd-guide-section-renderer:nth-child(3)
{ display: none !important; }

ytd-rich-shelf-renderer { display: none !important; }

#comments { transition: 0.30s ease-in-out; filter: blur(10px); }
#comments:hover { filter: blur(0px); }

#count,
#owner-sub-count
{ display: none !important; }
  
}
