css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gametrailers.com") {

/* Uncomment to remove the highlights box from the top of the page: */
/* .MassiveContainer { display:none !important; } */


#TOP_ADROW, .sponsored_links_title_container, .sponsored_links, .botfoot, .SpotlightContainer, .PlatformTitleBox, .copyright, .gamepage_right_content, .MovieScore, .ContentFooter, .ListingTitle, .SidePanel, .SearchAjaxFooter, iframe, .MovieInfo, #movies, .Bottom, .leftnav_container, .right_content, #minimassive0, .newestlist_bottom, .rightthin_content, .GameInfoTable { display:none !important; }

body { background:#EAE6E5 !important; }

.ContentWrapper, .ContentBodyWrapper, .ContentBody, .ContentListings, .GamePageCenterContent, .ContentHeader, .topnav_text { width:auto !important; background:transparent !important; }

.GameInfoSingleRating { height:auto !important;  width:auto !important; text-align:center; background:transparent !important; }

.GameInfoSingleRating .GameTitle { color:#203B71 !important; font-size:2em !important; }

.gamepage_content_sort_select { margin: .15em 1em !important; }

.Heading { -moz-border-radius:.5em .5em 0 0; }

#header{ margin-bottom:.5em !important; -moz-border-radius:0 0 1em 1em; }
#Footer, .botnav { margin-top:.5em !important; -moz-border-radius:1em 1em 0 0; }

.ContentListings { -moz-column-count:2; }

.MassiveBox, .MassiveImg { height:12em !important; }

.MassiveContainer { width:657px !important; margin:0 auto .5em !important; }

.MassiveImg { background-position:0 75% !important; }

.MassiveDesc { max-height:12em; overflow:auto !important; }

.RegularPlayer { margin:auto !important; }

.ContentMenus { float:none !important; margin:auto !important; }
.ContentBodyWrapper, .newestlist_container { border-top:1px solid #CCC !important; }
#AllMedia div[style="margin: 2px;"] > table { padding:0 0 .3em 0 !important; border-bottom:1px solid #CCC !important; }
.Splitter { display:none !important; }

.MoviePagination { text-align:center !important; }
}

@-moz-document url-prefix("http://www.gametrailers.com/video/") {

div[style="display: inline; margin-left: 20px; width: 640px; float: left;"] { float:none !important; }

}

@-moz-document url("http://www.gametrailers.com/") {

.center_content, .page_content { display:block !important; float:none !important; width:auto !important; }
.page_middle { width:auto !important; }

#massive { width:485px !important; margin:auto !important; }

.newestlist_container { -moz-column-count:2; width:auto !important; }

.newestlist_container, .newestlist_under_top, .newestlist_content { background:transparent !important; }

.newestlist_content div[align], .newestlist_content .clear_both { display:none; }
.newestlist_content > table { padding:.25em 0 !important; border-bottom:1px solid #CCC !important; }

.newestlist_top { margin:auto; }
}
