css:
@-moz-document domain("halooglasi.com") {
.widget-ad-dialog .product-list-item .pi-img-wrapper, .widget-ad-list .product-list-item .pi-img-wrapper {
    max-width: 300px !important;
    max-height: 360px !important;
    width: 300px;
    height: 360px;
    padding: 0px !important;
    margin: 0px !important;
}

img.resized-image {
    width: 100%;
    height: 100%;
    object-fit: cover !important;
}





img.resized-image {
    left: 0 !important;
    top: 0 !IMPORTANT;
    margin-left: 0px !important;
    object-fit: cover;
}

.widget-ad-dialog .product-list-item, .widget-ad-list .product-list-item {
    height: 300px;
}


}
