css:
@-moz-document url-prefix("https://pinterest"), url-prefix("https://www.pinterest") {
body, div[style="height:80px;width:100%"], div[data-root-margin="search-improvements-bar"], #searchBoxContainer, div[data-test-id="user-follow-button"] > button {
    background-color: #222 !important;
    color: #eee !important;
}
#searchBoxContainer div, [data-test-id="description-content-container"], [data-test-id="description-content-container"] > div > div > div, div[data-test-id="closeup-action-items"] {
    background-color: #333 !important;
}
div[data-test-id="lego-icon-wrapper"] svg[role="img"], div[data-test-id="boardSelectionDropdown"] svg, h1, .richPinInformation span, .deprecatedTextSizeXL span, div, span {
    color: #eee !important;
}
div[data-test-id="lego-icon-wrapper"]:hover, div[data-test-id="boardSelectionDropdown"]:hover {
    background-color: #222 !important;
}
.HeaderContent input, .HeaderContent span, .HeaderContent a, .HeaderContent svg {
    color: #eee !important;
    border-radius:22px !important;
}
.newsHubSidebarWrapper > div, div[data-test-id="header-background"] div:hover {
    background-color: #222 !important;
}
.appContent button {
    background-color: #333 !important;
    color: #eee !important;
}
.mainContainer div, button, a, input {
    border-radius:0 !important;
    box-shadow:none !important;
    color: #eee !important;
}
.HeaderContent div, button, a, input {
    border-radius:2px !important;
    box-shadow:none !important;
}
div[style='padding-bottom: 16px; padding-left: 8px; padding-right: 8px;'] {
    padding-bottom: 12px !important;
    padding-left: 3px !important;
    padding-right: 3px !important;
}
div[style*="border-radius:32px"] {
    box-shadow:none !important;
}
div[data-test-id="visual-content-container"], div[data-test-id="visual-content-container"]+div {
    width: 100% !important;
}
div[data-test-id="visual-content-container"]>div {
    min-width: 600px !important;
    height: 100% !important;
}
div[data-test-id="closeup-body-image-container"] {
    width: 100% !important;
    height: 100% !important;
}
div[data-test-id="closeup-title"]+div>div:nth-child(3), div[data-test-id="closeup-title"]+div>div:nth-child(5), div[data-test-id="visual-content-container"] + div > div >div:nth-child(3), div[data-test-id="related-modules-header"] {
    display:none!important;
}
div.appContent>div {
    padding-top:60px!important;
}
div[data-test-id="PinBetterSaveDropdown"] {
    width: 100% !important;
}
div[data-test-id="better-save"] >div >div {
    /* reserve space for large save button */
    height: 120px !important;
}
div[data-test-id="better-save"] >div >div >div >div {
    /* make save button large */
    display: block !important;
}
div[data-test-id="better-save"] >div >div >div >div >div:nth-child(1) {
    padding-bottom: 0px !important;
}
div[data-test-id="better-save"] >div >div >div >div button {
    width: 100% !important;
}
div[data-test-id="saved-info"] {
    margin-top: 14px !important;
    margin-bottom: 14px !important;
    margin-right: 10px !important;
    padding:0!important;
}
div[style="height:80px;width:100%"] {
    height: 60px!important;
}
::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-track, ::-webkit-scrollbar-track-piece {
    background: #fff;
}
::-webkit-scrollbar-thumb {
    background: rgba(0, 0, 0, 0.2)!important;
    border-radius: 0px;
}
/* SAVE FORM - SELECT BOARD */
 div[style*="stroke: rgb(239, 239, 239); visibility: visible"] {
    background-color: #333 !important;
    border-radius:3px !important;
}
div[style*="stroke: rgb(239, 239, 239); visibility: visible"] > div >div>div {
    background-color: #333 !important;
}
div[style*="stroke: rgb(239, 239, 239); visibility: visible"] > div >div {
    background-color: #333 !important;
    border: none !important;
}
div[style*="stroke: rgb(239, 239, 239); visibility: visible"] > div >div>div>div>div>div>div>div>div:nth-child(1) div, div[style*="stroke: rgb(239, 239, 239); visibility: visible"] > div >div>div>div>div>div>div>div>div:nth-child(3) > div >div >div>div div[title] {
    color: #eee !important;
}
div[style*="stroke: rgb(239, 239, 239); visibility: visible"] > div >div>div>div>div>div>div>div>div:nth-child(3) > div >div >div>div {
    background-color: #333 !important;
}
}
