@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ALTERNATE IMAGE */
.gm-icon {
margin-top: 0px !important;
list-style-image: url("data:image/png;base64,") !important;}

.gm-icon[status|="gm-account-state-logged-out"] {
-moz-image-region: rect(0px 16px 13px 0px) !important; /* Logged out */}

[unread="0"] .gm-icon[status|="gm-account-state-logged-in"] {
-moz-image-region: rect(0px 48px 13px 32px) !important; /* Logged in */}


[unread]:not([unread="0"]) .gm-icon[status|="gm-account-state-logged-in"] {
-moz-image-region: rect(0px 64px 13px 48px) !important; /* New mail */}

.gm-icon[status|="gm-account-state-error"] {
-moz-image-region: rect(0px 32px 13px 16px) !important; /* Error */}

/* THROBBER */
.gm-icon[status="gm-account-state-connecting"] {
list-style-image: url("chrome://global/skin/icons/loading_16.png") !important;
-moz-image-region: rect(0px 16px 16px 0px) !important;}
