/* CardView */
#reader_pane.reader_pane_view_style_3.reader_pane_sinner{
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(/*[[cv_wi]]*/, 1fr));
  grid-gap: 0 20px;
  justify-content: center;
  padding: 0 25px;
}

#reader_pane.reader_pane_view_style_3 .article_tile{
  padding-bottom: 25px;
  height: auto;
  width: auto;
  max-width: 100vw;
}
#reader_pane.reader_pane_view_style_3:not(.tiles_wrapper_2) .article_tile{
  width: auto;
}
  #reader_pane.reader_pane_view_style_3 .article_tile_content_wraper{
  height: auto;
}
#reader_pane.reader_pane_view_style_3 .article_tile_title{
  font-size: 15px;
  line-height: 1.4;
}
#reader_pane.reader_pane_view_style_3 .article_tile_content_wraper:not(.no_pic) .article_title_link{
  -webkit-line-clamp: /*[[cv_tl]]*/;
}
#reader_pane.reader_pane_view_style_3 #all_read_message{
  grid-column-start: 1;
  grid-column-end: -1;
  left: 0 !important;
  right: 0;
}
#reader_pane.reader_pane_view_style_3 :not(.article_tile){
  grid-column-start: 1;
  grid-column-end: -1;
}
.article_tile_footer{
  display: flex;
  padding: 0 8px;
  min-height: 25px;
}
.article_tile_footer > div{
  margin: 0;
  padding: 0;
}
.article_tile_footer .article_tile_footer_left_buttons{
  overflow: hidden;
  flex-grow: 1;
}
.article_tile_footer_feed_title{
  width: auto;
  max-width: none;
}
.article_tile_footer_right_buttons{
  display: flex;
  flex-grow: 0.01;
}

/* ADcleaner */

div[id$=_overlay]:not([id*=dialog]):not([id^=xconfirm]):not([id^=xinfo]):not([id^=xalert]),
.block_article_ad,
.ad_footer_remove{
  display: none;
}
#reader_pane.reader_pane_sinner{
  padding-right: 0;
}

}
