@-moz-document url-prefix("http://headlines.yahoo.co.jp/hl") {
/* template v4 beta update 2008/04/14 r2 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
body > table[width="100%"]
, body > table[width="100%"] div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
body > table[width="100%"] * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
body > table[width="100%"] a:link { color : #0000ee !important; }
body > table[width="100%"] a:visited { color : #551a8b !important; }
body > table[width="100%"] a:hover { color : #ee0000 !important; }
body > table[width="100%"] a:hover { background-color : transparent !important; }
/* /destroy div layout */

#uhdMain
, #wrapper
, #wrapper + div
, .yngrouplink
, #ynhead
, #ynsubnav
, #ynsubnav2
, .box_ct
, td[style="padding: 0px; width: 300px; vertical-align: top;"]
, .ynbox_left
, .ynbox
, .yntitle_sub6
, .yntitle_sub7_r
, .yntitle_sub4b
, .yntitle_sub9
, .yntitle_sub9 + div
, .yntitle_sub9 + div + div
, .list_ya + div + br + table
, .list_ya img
, td[style="padding: 0px 20px 0px 0px; vertical-align: top;"] > img:first-child[style="margin-bottom: 8px;"]
{ display : none !important; }

h2
, .yjSt
{ padding : 0 !important; }

}
