css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://archive-media-2.nyafuu.org/wg/image/1483/83/1483831582174.gif");
    background: fixed;
  }

  html
  {
    background: fixed url("https://archive-media-2.nyafuu.org/wg/image/1483/83/1483831582174.gif") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 100%;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #da08ff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #8808ff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .tc-t
  {
    color: #b908ff;
  }

  .theme--pDefault .profile-body
  {
    color: #b108ff;
  }
}

@-moz-document domain("nitrotype.com")
{
  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%;
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg,#2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
    background-size: 100%;
  }

  .card--a
  {
    background-color: #fff0;
    color: #ff02f8;
  }

  .dash-copy
  {
    color: #b203fd;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  .dash-copyContainer
  {
    background: #000;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #a200ff;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #db08bd;
    color: #fff0;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ee5f02 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://cdn.discordapp.com/attachments/740590203042791455/782067638138175488/31367.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }

  .btn--primary
  {
    background-image: url("https://wallpaperaccess.com/full/130206.jpg");
    color: #fff;
    background-size: 100%;
    background-color: #000 !important;
  }

  .btn--secondary
  {
    background-image: url("https://wallpaperaccess.com/full/130206.jpg");
    color: #fff;
    background-size: 100%;
    background-color: #000 !important;
  }

  .growl--a
  {
  }

  .profile-carContainer.card.card--a.card--shadow-l
  {
    background-color: #fff0;
  }

  .raceResults-awards
  {
    background: #fff0;
  }

  .raceResults-close.raceResults-close--minimizer
  {
    background: #dfdec9;
  }

  .g.raceResults-grid
  {
    background: #fff0;
  }

  .dash-content
  {
  }

  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  path:first-child, path:nth-child(7)
  {
    fill: #870eaa;
  }

  .raceResults-titles
  {
    display: none;
  }

  .raceResults.raceResults--default
  {
  }

  .structure-ad
  {
    display: none;
  }

  .garage
  {

  }

  .ad--ldrbrd_l
  {
    display: none;
    width: 970px;
    height: 90px;
  }

  .profile-goldLogo
  {
    opacity: 0;
  }

  .nav
  {
    background: url(https://i.pinimg.com/originals/ec/ec/a7/ececa7466223afe2c32d86b37b999419.png);
    border-radius: 0px;
  }

  .profile-goldBorder
  {
    opacity: 0;
  }

  .theme--pDefault.is-gold.profile-goldLogo::before
  {
    opacity: 0;
  }

  .chart--a .chart-line circle
  {
    fill: #000 !important;
    stroke-width: 0 !important;
    stroke: #fff !important;
    display: none;
  }

  .well--p.well--l_p
  {
    background: linear-gradient(to right, lightblue, #83FF83);
  }

  .achievement-is-complete
  {
    display: none;
  }

  .prog-points
  {
    color: #fff;
  }

  .card-cap.bg--gradient
  {
    background: linear-gradient(to right, lightblue,#83FF83 );
  }

  .twb
  {
    color: #fff;
  }

  .btn.btn--xs.btn--d.btn--float
  {
    background: white;
    color: #0000;
  }

  .card.card--e.card--sq.card--shadow-l.card--f.mbf.dhf.card--bc-emerald
  {
    background: #ff00;
    border: none;
  }

  .h5.tc-ts
  {
    color: #fff;
  }

  .btn.btn--stroke-i.btn--xs.btn--float.btn--fw
  {
    background: #006457;
    border: none;
  }

  .btn--stroke-i.mbf.dhf
  {
    color: #fff;
  }

  .btn.btn--c.btn--xxs.btn--icon
  {
    position: absolute;
    right: 90px;
  }

  .h3.mbs
  {
    color: #fff;
  }

  .news-teaser
  {
    color: #fff;
  }

  .card-cap.bg--gradient
  {
    background: url("https://wallpaperaccess.com/full/527886.jpg");
  }

  .card.card--b.card--shadow.card--f.card--grit.well.well--b.well--l
  {
    border: none;
    box-shadow: none;
    opacity: 1;
    color: #fff;
  }

  .nav-link
  {
    color: #fff;
  }

  .well--p.well--l_p
  {
    background: url(https://wallpaperaccess.com/full/527886.jpg);
  }

  .card.card--b.card--login.card--shadow.card--grit.card--f.card--o.cell.cell--s
  {
    background: linear-gradient(to right, #2d2186, #c36381);
  }

  .btn.btn--google.btn--fw.btn--icon.btn--float
  {
    color: #b1ade6;
  }

  .nav
  {
    border: none;
  }

  .footer-nav
  {
    background: url(https://wallpaperaccess.com/full/527886.jpg);
  }

  .footer-copyright
  {
    background: linear-gradient(to right,#0e1b2b,#194ca3);
  }

  .btn.btn--a.btn--fw.btn--float
  {
    background: #fff;
    color: #000;
    text-shadow: none;
  }

  .footer-card.card.card--c.card-shadow.tac.mbf
  {
    background: url(https://wallpaperaccess.com/full/130206.jpg);
  }

  .btn.btn--a.btn--float
  {
    background: white;
    text-shadow: none;
    color: #ffa200;
  }

  .profile-goldBorder
  {
    opacity: 0;
  }

  .split.split--eq.split--divided.split--divided_big.split--stretch.split--center.card-loginContent
  {
    background: linear-gradient(to right,#093266,#1988a3);
  }

  .btn--a
  {
    background-color: #fff;
  }

  .news--solo .news-body p
  {
    color: #FFFDFD;
    line-height: 1.8;
  }
}
