css:
@-moz-document domain("youtube.com") {
.ytp-swatch-background-color {
    background-image: -webkit-gradient( linear, left top, right bottom, color-stop(0, #44f), color-stop(1, #4f4));
}

.ytp-hover-progress-light {
    background: rgb(206, 230, 8, 0.5);
}

.ytp-load-progress {
    background: rgb(120, 120, 238, 0.3);
}
}
