css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wrongplanet.net") {
/* computerlove yay */

/* ------------ LEFT AD ----------------------- */
.shadowbox
{display: none !important;}


/* ------------ FORUM VIEW ----------------------- */
IMG[width="46"][title="New posts"]
{height:15px !important; width:25px !important;}



/* ------------ SUBFORUM VIEW ----------------------- */
IMG[title="No new posts"], IMG[title="Goto page"]
{display: none !important;}

/* ------------ MESSAGE VIEW ----------------------- */
.thRight, TH.thLeft, TABLE[width="18"], IMG[title="Post"], [href="#top"], [nowrap="nowrap"][height="28"], [width="150"][valign="middle"][align="left"]
{display: none !important;}

/* ------------ COLORS ----------------------- */
a.Nav:visited {color:purple !important;}
a.topictitle:visited {color:purple !important;}
a:active {color:#ffffff !important;}
a.topictitle:hover {color:blue !important;}


/* ------------ LINK - ACCESIBILITY COLORS ----------------------- */
/*
a.topictitle:link {text-decoration:underline !important;}
*/
a.postlink:link {color:blue !important; text-decoration:underline !important;}
a.postlink:visited {color:purple !important; text-decoration:underline !important;}

/*
td.row1 {background:#f7f7f7 !important;}
td.row2 {background:#e9e9e9 !important;} 
*/


/* ------------ MESSAGE VIEW: User info ----------------------- */
IMG[src^="modules/Forums/images/ranks/"]
{display: none !important;}
IMG[vspace="6"]
{height: 50% !important; width: 50% !important;}


/* ------------ bottom stuff ----------------------- */
TABLE[cellspacing="3"][cellpadding="0"][border="0"][align="center"]
{display: none !important;}


/* ------------ Polls, Stickies, etc ----------------------- */
B {color:#224499 !important;}
A [href="javascript:openwindow()"]
{display: none !important;}



/* -------- HEADER ---------- */
A[href="http://www.AspieAffection.com"], IMG[src="images/rssforumsblue.png"], A[href="http://wrongplanet.net/supporters.htm"]
{display: none !important;}

img[src="http://cdn.wrongplanet.net/images/titlelogocentered.jpg"]{
height:26px!important;
padding-left:118px!important;
background-image:url("data:image/jpeg;base64,")!important;width:0!important}


/* -------- FOOTER ---------- */
FONT.footmsg, SPAN#ad336
{display: none !important;}

}
