@-moz-document url-prefix("https://classroom.google.com"), url-prefix("https://meet.google.com/") {
body {
    background-image: url(https://images6.alphacoders.com/112/thumb-1920-1123219.jpg);
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center center;
}

.joJglb {
    background: black;
}

.OX4Vcb {
    background-color: black;
}

.TQYOZc {
    background-color: black;
}

.SZ0kZe {
    background-color: #222121;
}

/* width */
::-webkit-scrollbar {
  width: 7px;
}

/* Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 5px gray; 
  border-radius: 10px;
  background: #2b2525; 
}
 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #141111; 
  border-radius: 10px;
}
}
