css:
@-moz-document domain("toyokeizai.net") {
/**
 * 東洋経済オンライン✨スッキリ (June2021)
 */

/* 禁止タグ */
iframe,
video {
    display: none!important;
}

/* 背景広告スペース除去 */
body {
    margin-top: 0px !important;
    padding-top: 0 !important;
}

/* TOPページ スライド画像右側の記事表題リスト */
div#photo-wrapper + div.top-news1-list{
    float:left
}

/* TOPページ 広告記事要素 */
div#top-news2 > ul > li.pr, 
div#top-news3 > ul > li.pr, 
div.clearfix > ul.clearfix > li.pr{
    display: none!important;
}

/* TOPページ 記事サムネイル(大) */
ul.clearfix li.wd217 {
    clear: none!important;
    margin: 12px 15px 0 0!important;
    height: 192px;
}
/* TOPページ 記事サムネイル(小) */
div#top-news3 > ul >li.wd152,
div#scroll-container > ul >li.wd152 {
    margin: 12px 15px 0 0!important;
}

/* 右サイド */
div#sub-column {
    display: none;
}
/* 記事 横幅 */
div#main-column {
    width: 1000px;
}
/* (記事タイトル下の)次へボタン */
div.feed-page {
    display: none;
}
/* SNSボタン・メール・印刷ボタン */
div.facebook,
div.twitter,
div.mail,
div.print {
    display: none!important;
}
/* 文字サイズ */
div.font-b,
div.font-s {
    display: none!important;
}

/* ページナビ(AutoPagerize使用時は非表示推奨) */
/* 次ページ、ページ番号 */
/*
div.article-next-page,
div.mp-ie-end
{
    display: none;
}
*/

/* 記事下部 広告ビデオ */
div#div-gpt-ad-inread {
    display: none;
}

/* 記事下部 */
div.article-tags,
/* キーワード */
div#related-article,
/* 関連記事 */
div#topic-board,
/* トピックボード */
div#genre-ranking-article,
/* 人気記事 */
div#article-series-list,
/* 連載記事 */
div#article-bottom
{
    display: none;
}

/* 背景広告 */
div.jpx-wp-wrapper,
div#jpx-wp-front-container {
    display: none;
}

/* トピックス(広告) */
div.tktopics{
    display: none;
}

/* TOPページ トレンドライブラリー*/
div#trend-library, 
div#adexchange {
    display: none;
}
}
