css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
ITpro
http://itpro.nikkeibp.co.jp/
*/

@-moz-document url-prefix("http://itpro.nikkeibp.co.jp/article/") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
body > table
, body > table div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
body > table * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
body > table a:link { color : #0000ee !important; }
body > table a:visited { color : #551a8b !important; }
body > table a:hover { color : #ee0000 !important; }
body > table a:hover { background-color : transparent !important; }
/* /destroy div layout */

body > a
, body > img
, body > table[width="999"]
, body > map + table
, #subNaviArea
, #pulldownMenuArea + table
, #keywordpop + table + a + table
, #keywordpop + table + a + table + table
, #keywordpop + table > tbody > tr[valign="bottom"]
, #keywordpop + table > tbody > tr > td[width="413"]
, #keywordpop + table > tbody > tr > td[width="1"]
, #keywordpop + table > tbody > tr > td[width="586"] > *
, h1 + table
, .page_num + br
, .page_num + br + img + br
, #kiji + br + div[align="right"] + img + br + img + br + table
{ display : none !important }

#keywordpop + table > tbody > tr > td[width="586"] > #change_url
{ display : block !important; }

a[href="#top"]:before
{ content : "ãƒšãƒ¼ã‚¸ãƒˆãƒƒãƒ—ã¸"; }

}
