css:
@-moz-document domain("ryanscomputers.com") {
/* your code goes here */



/* Main layout */
.container {
    width: 97vw;
}

/* Navbar dropdown menu */
#navbar .menu-large .megamenu {
    max-width: 95vw;
}

/* Landing page carousal */
@media (min-width: 1280px) {
    .main-slider-wrapper .container {
        width: 50vw;
    }
    .main-slider-wrapper {
        zoom: 30% !important;
    }

    .main-slider-wrapper:hover {
        zoom: 90% !important;
    }
}


/* Landing page deals */
.hot-deals .row {
    display: flex;
    justify-content: center;
}

.hot-deals .row .col-md-3 {
    width: 13% !important;
}

.h1,
.h2,
.h3,
h1,
h2,
h3 {
    margin-top: 0 !important;
    margin-bottom: 0 !important;
}

/*  Filter  */
.col-lg-3 {
    width: 15%;
}

/* LIST start */
/* List page top thumbnail */
.category-thumb {
    display: flex;
    justify-content: center;
}

.category-thumb img {
    width: 20% !important;
}

.category-thumb img:hover {
    width: 50% !important;
}

.main-list .title {
    margin-left: -10px;
}

.main-list .title h2 {
    font-weight: inherit;
}

.default-brand-filters {
    padding: 5px 0;
}

.top-filter-style {
    margin-bottom: 5px !important;
    padding-left: 5px !important;
}

.col-lg-9 {
    width: 85%
}

.col-lg-4 {
    width: 12% !important;
    padding-right: 5px;
    padding-left: 5px;
}

#produt-container {
    font-size: 12px !important;
}

.product-content-info {
    padding: 0 10px !important;
}

.product-head {
    display: none !important;
}

.regular-price-grid:before,
.special-price-grid:before {
    content: none !important;
}

.regular-price-grid,
.special-price-grid {
    display: flex;
    margin-left: 0 !important;
}

.regular-price-grid {
    text-decoration: line-through;
    color: #962D2D;
}

.special-price-grid {
    color: #224230;
}

.add-to-cart:before,
.compare-btn-new:before {
    font-size: 11px !important;
}

.add-to-cart:before {
    content: 'âž• Cart';
    font-size: 0.6rem !important;
}


/* LIST END */
/* PAGINATION start */
.toolbar {
    position: absolute;
    top: -55px;
    right: 4%;
    width: auto;
    background-color: #A6CE39;
}

.toolbar .pager ol .current {
    background: #224230;
    border-color: #224230;
}

.toolbar .pager ol li a:hover {
    background-color: #447057;
    border-color: #224230;
}

.show-by,
.sort-by {
    margin: 2px 7px;
}

.toolbar .pager {
    padding: 2px 0 2px 0!important;
}

/* PAGINATION end */
/* Details page start */
.product-content {
    min-height: 140px;
}

.owl-carousel .owl-item img {
    width: 50% !important;
}

/* Details page end */
.emi-text {
    display: none !important;
}

/* Useless article */
.copyright-wrapper div:last-child {
    display: none;
}

/* Footer */
.footer-wrapper .container .row .footer-box .col-lg-4 {
    width: 33.33% !important
}
}
