css:
@-moz-document domain("ejje.weblio.jp") {
/**
 * ejje.weblio.jp - Weblio辞書 Remove Ads
 * Sept.2022
 */

/* IFRAME禁止 */
iframe{
    display: none;
}

/* 右サイド */
div#side{
    display: none;
}

/* メインコンテンツ */
div#contentBody{
    margin:0 auto;
    position:static;
    width:1000px;
}
div#main{
    margin:0 auto;
    float:none;
    margin-bottom: 0px!important;
}


/* TOPページ 急上昇ワード */
div#headBoxCmk,
/* TOPページ 上部広告 */
div#main > div[style*="margin-bottom:20px;"],
/* TOPページ プレミアムサービス */
div.prmmSrvc{
    display: none;
}

/* シェアする・広告を消すボタン */
td.topicR{
    display: none;
    
}

/* 初めての転職ならセレクタ */
div#learning-level-table-wrap + div[style="margin:10px 0 10px 5px;"],
/* 無料でweblio単語帳を使い放題で利用できます */
div#summary > div[style="margin:10px 0 10px 5px;"]
{
    display: none;
}

/* (辞書の意味の直下)広告 */
div#summary + div.mainBlock,
/* 関連キーワード */
div#summary + div.mainBlock +div.related-word-section,
/* Weblio英会話 広告(直前に関連キーワードがない) */
div#summary + div.mainBlock + div + div.mainBlock,
/* Weblio英会話 広告(直前に関連キーワードがある) */
div#summary + div.mainBlock + div.related-word-section + div + div.mainBlock,
/* Weblio無料会員登録 */
div.free-member-features,
/* 広告 */
div.flex-rectangle-ads-frame,
div#ejjeLeftBottomLeftAdId,
div#ejjeLeftBottomRightAdId
{
    display: none!important;
}

/* フッター アンケート*/
table#footBox div[style="margin-bottom:10px;"],
table#footBox div#fdbkWrp,
table#footBox div.footer_banner{
    display: none!important;    
}

/* ページ下部の常時表示広告 */
div#footFixAdBar {
    display: none!important;    
}
}
