css:
@-moz-document domain("dot.asahi.com") {
/**
 * AERA.dot (june2021)
 * remove ads from dot.asahi.com
 */

/* 禁止タグ */
iframe{
    display: none;
}

/* *** TOPページ 整形 *** */
div.topPicupThumbAreaWrapper{
    float:left ; /* TOP画像の記事 */
}
/* TOPページ 記事見出しリスト */
ul.topPicupWrapperList{
    margin-left: 178px;
}
/* TOPページ 記事見出しリスト下テキスト広告 */
div#interest_ad_3line_wrapper {
    display: none;
}
/* TOPページ 縦配置 記事画像 */
div.topMediaWrapper:nth-child(3) {
    margin-left: 178px;
}
/* TOPページ ジャンル見出し */
h2.topGenreTitle {
    clear: both;
}
/* TOPページ ジャンル記事一覧 */
a.topGenreListAllLnk {
    margin-right: 178px;
}

/* TOPページ (コラムの次の) おすすめ記事(広告) */
a[href="/column/"] + h2.topGenreTitle,
div.topGenreThumbList{
    display: none;
}

/* ページナビ(AutoPagerize使用時は非表示推奨) */
/*
nav{
    display: none;
}*/

/* ページヘッダー */
img.AeradotMailmagBnrImg{
    display: none;
}
/* ページ右サイドバー */
div.sidebar1{
    display: none;
}
/* コンテンツ横幅*/
div.content{
	width:1000px;
}

/* 記事内 広告 */
div.interest_ad_3line,
div.photoAtclAreaRectangleAd,
div#atclAreaContRect{
    display: none;
}

/* [続きを読む]の下の広告 */
div.MarginT20 > div[align="center"]{
    display: none;
}

/* 関連記事 */
div.atclTitleAreaRecommendarticle, /* おすすめ記事[見出し] */
ul.relatedArticleThumbList,/* おすすめ記事[内容] */
div.atclTitleAreaRelatedArticle,/* 関連記事[見出し] */
ul.relatedArticleThumbList,/* 関連記事[内容] */
div.atclTitleAreaSocialmedia, /* 記事のコメント[見出し] */
div.atclTitleAreaAccessRanking, /* アクセスランキング[見出し] */
div.rankingAreaAtcl, /* アクセスランキング[内容] */
div#outbrain_widget_0,
div#outbrain_widget_1{
    display: none;
}

/* AREA広告 */
div.photoAtclAmazonArea{
    display: none;
}

/* ページフッダー */
div.adAreaSuperBanner{
    display: none;
}
}
