css:
@-moz-document domain("news.ycombinator.com") {
html {
	--subtle: #444;
	--subtler: #888;
}

body {
	margin: 0;
}

* {
	font: inherit !important;
}

.votearrow {
	height: auto;
	width: auto;
	
	background: none;
}

.votearrow::before {
	width: 0.4em !important;
	height: auto;
	
	font-size: 0.6em;
	padding: 0 0.2em;
	
	content: "â–²";
	color: var(--subtle);
}

.comhead, .subtext {
	font-size: 0.8em !important;
	color: var(--subtle);
}

.comhead a:link, .subtext a:link {
	color: var(--subtle);
}
.subtext a:visited {
	color: var(--subtler);
}

tr.spacer {
	height: 0.5em !important;
}

#hnmain {
    width: 100%;
}
}
