/* ==UserStyle==
@name           Inny Bakemono/Ibuki
@namespace      shinden.pl
@version        1.0.0
@description    Styl wymaga wybrania stylu Bakemono w ustawieniach strony.
@author         Paranojka
==/UserStyle== */

@-moz-document url-prefix("https://shinden.pl/"), regexp("") {
.button, .button:visited {
    position: relative;
    z-index: 1;
    overflow: visible;
    display: inline-block;
    padding: .3em .6em .375em;
    border: 1px solid #222 !important;
    border-bottom-color: #222 !important;
    border-radius: 4px;
    margin: 0;
    text-decoration: none;
    text-align: center;
    font: bold 11px/normal'lucida grande', tahoma, verdana, arial, sans-serif;
    white-space: nowrap;
    cursor: pointer;
    color: #222;
    background-color: #840fad;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#840fad), to(#840fad));
    /* background-image: linear-gradient(#840fad,#840fad); */
    /* filter: progid:DXImageTransform.Microsoft.gradient(startColorStr='#840fad', EndColorStr='#840fad'); */
    /* -webkit-box-shadow: 0 1px 0 rgba(0,0,0,.1), inset 0 1px 0 #222; */
    box-shadow: 0 1px 0 rgba(0, 0, 0, .1), inset 0 1px 0 #222 !important;
}
.button-group .button {
    border-left-width: 0 !important;
    border-right: 1px solid #222 !important;
}
.logo {
    position: relative;
    height: 300px;
    /* background: url(https://i.imgur.com/K70Ihlm.jpg) no-repeat center; */
    background: linear-gradient(to bottom, #0a0a0a 36px, #840fad 36px, #0a0a0a 300px);
    background-size: cover;
}
.logo-char {
    content: url("https://i.imgur.com/XqFR5oI.png");
	-webkit-animation: text-focus-in 2s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
    animation: text-focus-in 2s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
}

.logo .logo-title img {
    content: url("https://i.imgur.com/59TIN7y.png");
}

.spolecznoscinet {
    display: none;
/*    visibility: hidden; */
}

.pb-logo {
    opacity: 0;
    display: none;
    visibility: hidden;
}
body {
    background: linear-gradient(to bottom, #0a0a0a 36px, #840fad 36px, #0a0a0a 300px);
	-webkit-animation: text-focus-in 300ms cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
	animation: text-focus-in 300ms cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
    color: #ffffff;
}
.nav-main, .nav-main a {
    color: #ffffff;
    letter-spacing: 0;
}

.l-container-primary, .l-main-contantainer {
    background-color: #0a0a0a;
    padding-bottom: 2px;
}
.l-container-secondary {
    background-color: #0a0a0a;
    padding-bottom: 5px;
}
.newTitle.newTitle-blue span {
    background: #0a0a0a;
}

.newTitle {
    border-bottom: 1px solid #383838;
    background: linear-gradient(to bottom, #840fad 50%, #760d9b 50%, #840fad 70%);
    border-radius: 20px 20px 0 20px;
    text-transform: uppercase;
    font-family: comfortaa,cursive;
    letter-spacing: 1px;
    margin: 3px 0;
    min-height: 26px
}

.newTitle span {
    position: relative;
    border-radius: 20px 20px;
    background: #0a0a0a;
    padding: 10px;
    top: 10px;
    border: 1px #840fad solid;
    border-bottom: none;
}

/* .newTitle span {
    position: relative;
    background: #0a0a0a;
    padding: 10px;
    top: 10px;
} */

.sliderButtons a {
    display: block;
    height: 30px;
    line-height: 32px;
    background: #0a0a0a;
    border: 1px solid #fff;
    border-radius: 5px;
    color: #fff;
    text-transform: uppercase;
    text-align: center;
    max-width: 120px;
    margin: 0 auto;
    font-weight: 100;
}
.seasons-list a {
    display: block;
    height: 42px;
    line-height: 42px;
    background: #0a0a0a;
    border: 1px solid #fff;
    border-radius: 5px;
    color: #fff;
    text-align: center;
    text-transform: uppercase;
    font-weight: 300;
    margin-bottom: 8px;
}
h1.page-title {
    background-color: #0a0a0a;
    margin: 0;
    padding: 0 10px;
    border-bottom: #840fad 1px solid;
    font-family: comfortaa, cursive;
}
.title-other, .episode-head {
    padding: 4px 7px;
    background-color: #0a0a0a;
}
.info-messages-box {
    background-color: #0a0a0a;
    border-radius: 0!important;
}
.info-aside {
    overflow: hidden;
    text-align: center;
    background-color: #0a0a0a;
    float: left;
    width: 255px;
    max-width: 255px;
    margin-left: 0%;
}
.data-view-table-strips tr:nth-child(2n), .data-view-even {
    background-color: #0a0a0a;
    border-bottom: #840fad 1px solid;
    border-top: #840fad 1px solid;
}
.nav-main-sub {
    margin-top: 0;
    padding: 5px 4px 0;
    position: relative;
    background-color: #0a0a0a;
    height: 26px;
}
.info-aside-title {
    margin: 0 15px;
    border-bottom: #840fad 1px solid;
    text-transform: uppercase;
    font-size: 13px;
    font-family: comfortaa, cursive;
}
.av-rank {
    background: #0a0a0a none repeat scroll 0 0 padding-box;
    border: 1px solid #840fad;
    border-radius: 5px;
    display: block;
    float: left;
    margin-right: 10px;
    max-height: 200px;
    overflow: hidden;
    -webkit-transition: all .2s ease .2s;
    transition: all .2s ease .2s;
    width: 100px;
}

.info {
    background-color: #840fad;
    border-color: #5c0a79;
    color: #0a0a0a;
    font-weight: 540;
}

.info-aside-img {
    border-radius: 10px;
}

.progressbar-demographic {
    position: relative;
    background-color: #3a3a3a;
    border-radius: 4px;
    margin: 0;
    height: 20px;
}

.info-top {
    background-color: #0a0a0a;
    overflow: hidden;
    padding: 0;
    margin: 0;
}

.info-top-table-highlight {
    background-color: #0a0a0a;
}

.figure-type {
    padding: 2px 0px;
    border: 1px solid #840fad;
}

.box-bottom {
    background-color: #0a0a0a;
    background-image: -webkit-gradient(linear,left top,left bottom,from(#0a0a0a),to(#0a0a0a));
    background-image: linear-gradient(top,#0a0a0a,#0a0a0a);
    /* filter: progid:DXImageTransform.Microsoft.gradient(GradientType=0,StartColorStr='#0a0a0a',EndColorStr='#0a0a0a'); */
}

.creator-form .navigation {
    text-align: center;
    padding: 6px;
    border-top: 1px solid #840fad;
    background: #0a0a0a;
    clear: both;
}

.unread-news, .unread-news .newTitle span {
    background-color: #0a0a0a;
}

.pagination ul li a {
    padding: 3px 8px;
    font-size: 14px;
    font-weight: 700;
    border: 1px solid #840fad;
    background: #0a0a0a;
    border-radius: 4px;
}

.l-footer {
    position: relative;
    margin-left: auto;
    margin-right: auto;
    left: 0;
    right: 0;
    padding-bottom: 38px;
    background: -webkit-gradient(linear,left top,left bottom,from(#0a0a0a),to(#840fad));
    background: linear-gradient(to bottom,#0a0a0a,#840fad);
    color: #fff;
    max-width: 100% !important;
}

.logo .logo-title img {
    padding-top: 40px;
	-webkit-animation: text-focus-in 2s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
	animation: text-focus-in 2s cubic-bezier(0.550, 0.085, 0.680, 0.530) both;
}

@-webkit-keyframes text-focus-in{0%{-webkit-filter:blur(12px);filter:blur(12px);opacity:0}100%{-webkit-filter:blur(0);filter:blur(0);opacity:1}}@keyframes text-focus-in{0%{-webkit-filter:blur(12px);filter:blur(12px);opacity:0}100%{-webkit-filter:blur(0);filter:blur(0);opacity:1}}

.top-bar .top-button {
    background: #0a0a0a;
    height: 30px;
    line-height: 30px;
    color: #fff;
    text-transform: uppercase;
    text-align: center;
    padding: 0 10px;
    display: inline-block;
    margin: 3px 10px 0;
}

.top-bar--nav-user {
    background: #0a0a0a;
    position: absolute;
    right: 10px;
    padding: 10px;
    display: none;
    width: 200px;
    z-index: 99;
    font-size: 17.6px;
    font-size: 1.1rem;
}

.top-bar .top-button--user {
    border-top: 1px solid #840fad;
    border-bottom: 1px solid #840fad;
    margin-top: 0;
}

.nav-list-sub>li {
    background: #0a0a0a;
    text-align: left;
    display: block;
    position: relative;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}

.is-active {
    background: #0a0a0a;
    color: #840fad;
    border: solid 1px #840fad;
}

html, button, input, select, textarea {
    color: #0a0a0a;
}

.warning {
    background-color: #840fad;
    border-color: #840fad;
}

.error {
    background-color: #840fad;
    border-color: #840fad;
}

.creator-form .warning div.help {
    color: #ffffff;
}

x-star-rating>.star::after {
    content: '\f006';
    color: #840fad;
    margin: 1px;
}

body>div[id^=c_window_] {
    margin: 0!important;
    display: none;
}

.modal-layer {
    position: fixed !important;
    background: rgba(10,10,10,.6);
    z-index: 999;
    margin: auto;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    display: none;
}

section.video-info {
    background-color: #0a0a0a;
    padding: 11.2px;
    padding: .7rem;
    border-radius: 0!important;
}

.pb-video-player, .pb-ad-video-player {
    width: 100%;
    background: #000;
    position: relative;
    object-fit: contain;
}

.pb-resp-width-max {
    display: block;
    width: 100%;
}
}
