@namespace url(http://www.w3.org/1999/xhtml);
/*
izaï¼šã‚¤ã‚¶ï¼
http://www.iza.ne.jp/
*/

@-moz-document domain("iza.ne.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#root
, #root div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#root * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#root a:link { color : #0000ee !important; }
#root a:visited { color : #551a8b !important; }
#root a:hover { color : #ee0000 !important; }
#root a:hover { background-color : transparent !important; }
/* /destroy div layout */

h1
, .speech
, #iza_userTools1
, #newsRelContents
, #google_ads
, #rectangle_box
, #iza_todayRcmBkm
, #secondery_contents
, #global2
, #global1
, td.relnews_s3
, td.iza_TPNW_s3
{ display : none !important; }

div.relation_infos table
, div.relation_infos table td.first_cell
, div.base_layout2 h2
, div.base_layout10 h2
{ border : 0 !important; }

div.base_layout2 h2
{ margin : 0 !important; }

th.link_type1, td.link_type1
, span.title
, span.title_type2
, div.base_layout10 h2
{ padding : 0 !important; }

}
