css:
@-moz-document domain("gendai.ismedia.jp") {
/**
 * 現代ビジネス✨スッキリ(gendai.ismedia.jp)
 * remove ads from gendai.ismedia.jp
 * /

/* IFRAME禁止 */
iframe{
    display:none!important;
}

/* 最新記事 要素 */
div.blockMain div.blockContainer div.blockContainer_left2 > div > div.elementArticleItem{
    margin-right:30px;
}

/* ページ左側 ホバーSNSボタン */
div.articleSidebarSns{
    display:none;
}

/* ページ上部イメージ縮小 */
div.articleFirstImage{
    background-size: contain;
	background-repeat: no-repeat;
    background-position: center;
    height:200px;
    max-width: 100%;
}
/* 右サイドバー */
div.blockContainer_right1{
    display:none;
}

/* 記事横幅指定 */
.blockContainer{
    width:1000px;
}
.blockContainer_left2{
    width:100%;
}
div.blockContainer{
    margin-bottom:0px;
}

/* 記事内画像　幅抑制 */
div.articleContents img{
    max-width:500px;
}

/* 記事上部広告 */
div[style="margin: 40px auto -60px auto;"]{
    display:none;
}
/* 記事内広告 */
div#GB_pc_inArticle, 
div#MG_pc_inArticle,
div.teads-inread {
    display:none!important;
}

/* 次ページ / ページナビ
  (AutoPagerize使用時は非表示) */
/*
div.nextpageTitle,
div.pagination{
    display:none;
}
*/

/* (ページナビ下の)広告 */
div#GB_pc_inread_mov,
div#GB_pc_inread_2,
div.sponsored_1,
div.sponsored_2,
div.sponsored_3 {
    display:none;
}

/* ページ下部ホバー広告 */
div#MG_pc_overlay {
    display:none;
}

/* 記事下部 SNSボタン */
div.articleBottomSns{
	display:none;
}
/* 広告要素の余白除去 */
div[style="min-height: 250px;"],
div[style="min-height:90px;margin-bottom:45px;"]{
    display:none;
}

div.elementSectionHeadingsWithSuffix,  /* 関連記事[見出し] */
div#logly-lift-widget, /* 関連記事[内容] */
div.relatedArticleBlock /* ホバー */
{
    display:none;    
}

/* 検索キーワード・広告*/
div#csw_block,
div.ob-smartfeed-wrapper{
    display:none;
}
}
