css:
@-moz-document domain("pillowfort.social") {
/*-----------------variables-----------------*/
/*palette generated using coolors.io : https://coolors.co/eeeeee-f2545b-a93f55-19323c-7d8cc4*/
:root {
	--c-ui: #19323C;
	--c-bg: #eee;
	--c-main: #7D8CC4;
	--c-main-hover: #526197;
	--c-acc: #F2545B;
	--c-acc-hover: #A93F55;
}

/*-----------------font tweaks-----------------*/
/*bigger, more readable font*/
body {
	font-family: "Trebuchet MS", Calibri, sans-serif !important;
	font-size: 1.5em;
}
/*bold links*/
a {
	font-weight: 700;
}

/*-----------------colors-----------------*/
body {
	background-color: var(--c-bg);
}
a,
.glyphicon,
.post-type,
button {
	color: var(--c-main) !important;
}
a:hover,
.glyphicon:hover,
button:hover {
	color: var(--c-main-hover) !important;
}
a.sidebar-row,
a.ng-binding {
	color: var(--c-acc) !important;
}
a.sidebar-row:hover,
a.ng-binding:hover {
	color: var(--c-acc-hover) !important;
}
.flag-button {
	background: none;
	color: var(--c-acc)
}
.header,
.navbar,
.comm-nav,
.comm-button,
.tabz,
.nav-tabs li:not(.active) a,
.tabz a:hover {
	background-color: var(--c-ui) !important;
}
.nav .active a {
	background-color: var(--c-bg) !important;
}
.btn,
btn:hover {
	color: var(--c-bg) !important;
	background-color: var(--c-main)
}
.btn-danger {
	background-color: var(--c-acc) !important;
}
.toggle-off {
	color: var(--c-ui) !important;
}
.timestamp {
	background: none !important;
	color: var(--c-ui) !important;
}
#resolved-btn {
	color: var(--c-ui);
	border-color: var(--c-ui);
}
.name_description .header {
	background: none !important;
}
.post-type.selected,
.post-type.selected .glyphicon {
	background-color: var(--c-acc) !important;
	color: var(--c-bg) !important;
}
.comment-subheader {
	color: white;
}

/*-----------------sidebar tweaks-----------------*/
/*make it flat*/
.sidebar,
.sidebar-box,
.sidebar-header,
.sidebar-toggle-indicator,
.sidebar-row,
.sidebar-icon {
	background-color: var(--c-ui) !important;
}

/*text changes*/
.sidebar,
.sidebar-row {
	text-transform: lowercase !important;
	font-variant: normal !important;
}
.box-text {
	font-size: 0.6em !important;
}
/*height changes*/
.sidebar {
	height: auto;
	padding-bottom: 15px;
	margin-top: -1px;
}
/*round corners*/
.sidebar,
.sidebar-row,
.sidebar-icon {
	border-radius: 0 0 40px 0;
}
/*-----------------round stuff-----------------*/
/* circle avatars*/
.avatar img {
	border-radius: 100%;
}
/*rounded posts*/
.post.main,
.nav-tab {
	border-radius: 40px;
}
/*rounded comments*/
.comment-form {
	border-radius: 20px;
}

/*-----------------remove borders-----------------*/
.sidebar,
.sidebar-box,
.sidebar-header,
.sidebar-toggle-indicator,
.sidebar-row,
.sidebar-icon,
.avatar img,
.main,
.nav-tab,
.navbar,
.ng-scope,
.btn,
.navbar-text,
.nav-logout,
.nav-tabs,
.nav-tabs a {
	border: none !important;
}

/*-----------------add subtle shadows-----------------*/
.avatar img,
.main {
	box-shadow: 0 0 15px rgba(0, 0, 0, 0.1)
}

/*-----------------random tweaks-----------------*/
/*images take full post width*/
.post .media,
.post .media img.full {
	width: 100%;
	max-width: 100%;
	padding: 0;
	margin: 0;
	border: none;
}
.post .media img.half {
	max-width: 100%;
	width: 49.6%;
	margin: 0;
}
/*remove gap between nav and header*/
.nav {
	margin-top: -1px !important;
}
/*navbar height fix*/
.navbar-right,
.navbar-right div {
	height: 53px;
}
}
