.sc-classic .insightsSidebarModule__button,

.inboxItem.sc-border-box.active:before
{
    filter: var(/*[[scqcc]]*/);
}
  
.sc-classic .inboxItem.active {
    border-left: none;
    margin-left: 2px;
}
.inboxItem.sc-border-box.active:before {
  content: "";
  height: 60px;
  position: absolute;
  border-left: 2px solid #f50;
