/* ==UserStyle==
@name         Minimal Youtube + WATCHED overlay
@version      20210919.09.45
@namespace    userstyles.world/user/amkitakk
@description  Hides like numbers, comments, chat, recommendations, and various little things I personally don't use; also adds a "watched" overlay on videos that have already been watched.
@author       amkitakk
@license      No License
==/UserStyle== */

@-moz-document domain("youtube.com") {
/*hide likes numbers*/
.ytd-toggle-button-renderer #text,
#sentiment {
	display: none;
}

/*hide comments*/
#comments {
	display: none
}


/*hide subject under description*/
.ytd-video-secondary-info-renderer.style-scope.sticky {
	display: none;
}


/*hide entire right sidebar(recommendations, live chat)*/
#secondary {
	display: none;
}

/*hide recs only*/
/*#related {
	display: none;
}
ytd-rich-section-renderer.ytd-rich-grid-renderer.style-scope {
	display: none;
}*/

/*hide chat*/
/*#chat {
	display: none;
}*/

/*watched overlay*/
.resume-playback-background,
ytd-thumbnail-overlay-resume-playback-renderer {
	background: rgba(0, 0, 0, 0.8) !important;
	position: absolute;
	left: 0;
	top: 0;
	display: block !important;
	height: 100%;
	width: 100%;
	pointer-events: none;
	text-align: center;
}

.resume-playback-background::before,
ytd-thumbnail-overlay-resume-playback-renderer::before {
	content: "WATCHED";
	color: white;
	font-weight: bold;
	box-sizing: border-box;
	width: auto;
	height: 100%;
	display: flex;
	pointer-events: none;
	font-size: 200%;
	flex-direction: row;
	align-items: center;
	justify-content: center;
}


/*remove watch later and add to queue*/
div#hover-overlays {
	display: none !important;
}


/*hide other youtube content*/
ytd-guide-section-renderer.ytd-guide-renderer.style-scope:nth-of-type(3) {
	display: none;
}


/*hide tags*/
#header > .ytd-rich-grid-renderer.style-scope {
	display: none;
}


/*hide end cards*/
.ytp-endscreen-content,
.ytp-ce-element {
	display: none !important;
}

/* hide mminiplayer button */
.ytp-miniplayer-button {
	display: none !important;
}

/* hide video view count */
.view-count {
	display: none;
}
#info-strings #dot {
	display: none;
}

/* hide sub count */
#owner-sub-count {
	display: none;
}

/* hide voice search */
#voice-search-button {
	display: none !important;
}

/* hide share button */
ytd-button-renderer.size-default.style-default.force-icon-button.ytd-menu-renderer.style-scope:nth-of-type(1) {
	display: none;
}

/* hide clip button */
ytd-button-renderer.size-default.style-default.force-icon-button.ytd-menu-renderer.style-scope:nth-of-type(2) {
	display: none;
}
}
