@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {

div.signature{
  display:none!important;
}

div.cfWrapper:hover>div.cfInnerWrapper>div.cfMessage>div.signature ,
div.cfWrapper:hover>div.cfInnerWrapper>div.cfMessageNoAvatar>div.signature {
  display: inline !important;
  border: none !important;
}

}
