css:
@-moz-document domain(mopaiv.com) {
	body, html {
      background: #1a1a1a;
    }

    .card, .navbar, .dropdown-menu, .dropdown-item, .Bf1Z775XMo, .Bf1Z775XMo *, .elS0k5beji, .elS0k5beji *, .form-control, .model, .r0TLQzHEUT, #tjGc8Gpp div, #wall div, #TAtPo9ZE div, #dis_feed div, #RCESYk1H div, .modal-content {
      background: #121212 !important;
      border-color: #121212 !important;
      color: #fff !important;
    }

    .card .form-control {
      background: #1a1a1a !important;
      border-color: #1a1a1a !important;
      color: #fff !important;
    }

    p, a, b, i, s, span[style*=font-size], div[style*=font-size], h1, h2, h3, h4, h5, h6, th, td, pre {
      color: #fff !important;
    }

    .nav-pills .nav-item span:hover {
      background: #1a1a1a !important;
    }

    .nav-pills .nav-item span.active:hover {
      background: #007bff !important;
    }

    .nav-pills span:hover {
      background: #1a1a1a !important;
    }

    .nav-pills span.active:hover {
      background: #007bff !important;
    }

    .hover-items:hover {
      background: #1a1a1a !important;
    }

    .card .card, .modal-content .form-control {
      background: #1a1a1a !important;
    }

    .modal-header, .modal-footer {
      border-color: #1a1a1a;
    }
}
