menupopup {max-height: 330px; /* or any height you want */} menupopup autorepeatbutton {display: none;}

#toolbar-context-menu:hover, menupopup menu:hover, menupopup menuitem:hover, popup menu:hover, popup menuitem:hover, #bookmarks-menu autorepeatbutton:hover, #personal-bookmarks autorepeatbutton:hover {color: #fff000 !important;}

:not(menulist) > menupopup > menuitem, menupopup > menu, popup > menuitem, popup > menu {margin-right: 0px !important;}
menupopup .menu-text, popup .menu-text { margin-left: -26px !important;} menupopup .menu-iconic-text, popup .menu-iconic-text { margin-left: 0px !important;}
menupopup .menu-iconic-left,popup .menu-iconic-left { width: 0px !important; margin-left: 2px !important; margin-right: 2px !important;}
menupopup, .menupopup, popup, .popup {-moz-appearance: none !important; background: #000  !important;-moz-border-left-colors: none !important; -moz-border-right-colors: none !important; -moz-border-top-colors: none !important; -moz-border-bottom-colors: none !important; -moz-border-radius: 0px !important;}

/* --- Schrift --- */
menupopup > menu,menupopup > menuitem,popup > menu,popup > menuitem {-moz-appearance: none !important;color: #fff !important; font-weight: bold !important;font-size: 14.5px !important;min-height: 18px !important; height: 24px !important; margin: 0px 0px 1px 0px !important;}

/* --- Hintergrund ---*/
#toolbar-context-menu, menuitem, menupopup, popup, menupopup menu, menupopup menuitem, popup menu, popup menuitem, #bookmarks-menu autorepeatbutton, #personal-bookmarks autorepeatbutton { background:#000 !important; border-top: 0px  !important; border-bottom: 0px !important; border-left: 0px !important; border-right: 0px !important;}

/* --- Highlightbalken ---*/
