popup menu > .menu-right,popup menupopup menu > .menu-right, menupopup >menu >.menu-right {-moz-appearance: none !important;min-width: 28px !important; max-width: 28px !important; min-height: 28px !important; max-height: 28px !important; margin: 6px 0px 0px 0px !important; opacity: 1 !important;}
