css:
@-moz-document domain("aternos.org") {
/* Main Background */
.content, .sidebar {
    background: url(https://i.ibb.co/1djSYcd/iaka-bg.png);
    background-position: center;
}

/* Header Size 1 */
.content h1 {
    color: #722EAA;
}

/* Positive Buttons */
.btn-green, .btn-success, .btn-main, .start-activity-help .start-activity, .start-activity.green {
    background: #722EAA;
}

/* Checkbox Switches */
.toggle:not(.inverted) input:checked+label:before, .toggle.inverted input:not(:checked)+label:before {
    background: #722EAA;
}

/* Software Type */
.software-type {
    background: #722EAA;
}
}
