css:
@-moz-document url-prefix("http://my.tablotv.com/") {
.livetv .schedule {
    height: 80%;
    z-index: 2000;
    opacity: 75%;
}
.livetv .preview {
    height: 20%;
}
html.player-maximized:not(.safari) .uistate-overlayPlayer.root-primary {
    opacity: 75%;
}
.component-takeover.overlayPlayer.isMin .video-player {
    overflow: visible;
    top: 45px;
    position: fixed;
    z-index: -1;
}
}
