/* ==UserStyle==
@name           Beta Battlegoats
@namespace      USO Archive
@author         ScalpelCat
@description    `a heavy WIP battle code breaker for Goatlings.I own no elements, they all up in the code.`
@version        20210621.6.44
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */
@-moz-document url-prefix("https://www.goatlings.com/battle/over"),
url-prefix("https://www.goatlings.com/battle/thebattle") {
	#content > center:nth-of-type(1) {
		font: 15pt;
	}
	#battle_items > div {
		margin-top: -150px;
	}
	#content > center > form > input[type=submit]:nth-child(5),
	input:nth-of-type(3) {
		margin-top: -250px;
		margin-left: -190px;
		margin-right: -10px;
		margin-bottom: -80px;
		position: fixed;
		height: 10em;
		width: 30em;
	}
	#content img[src*="https://www.goatlings.com/images/pets/"],
	img[src*="https://www.goatlings.com/images/enemies/"] {
		display: none;
	}
	#content a {
		font-family: Candara;
		font-size: 20px;
	}
	#battle-items{
		width: 200px;
	}
}

@-moz-document url-prefix("https://www.goatlings.com/battle/over") {
	#content img[src*="https://www.goatlings.com/images/items/"] {
		display: none;
	}
}
