.game-card-container
{
  background-color: #825e3e;
  position: relative;
  border-radius: 3px;
  float: left;
  height: 100%;
  margin: 0 auto;
  padding: 0;
  text-align: left;
  width: 100%;
  border-color: transparent transparent transparent #ecebec;
}

#GamesListsContainer > div
{
  background-color: transparent !important;
  width: 100% !important;
}

.section-content
{
  background-color: #825e3e;
  padding: 15px;
  position: relative;
}

@keyframes rainbow
{
  0%
  {
    background-color: #d9c59e;
    border-color: #d9c59e;
  }

  20%
  {
    background-color: #ba9f7b;
    border-color: #ba9f7b;
  }

  40%
  {
    background-color: #a38464;
    border-color: #a38464;
  }

  60%
  {
    background-color: #825e3e;
    border-color: #825e3e;
  }

  80%
  {
    background-color: #5e4025;
    border-color: #5e4025;
  }

  100%
  {
    background-color: #d9c59e;
    border-color: #d9c59e;
  }
}

.btn-common-play-game-lg
{
  animation: rainbow 10s infinite;
}

@keyframes spin
{
  from
  {
    transform: rotate(0deg);
  }

  to
  {
    transform: rotate(360deg);
  }
}

.icon-logo-r,
.light-theme .icon-logo-r,
.dark-theme .icon-logo-r
{
  background-image: url(https://i.pinimg.com/originals/23/bd/26/23bd26dcbbb36c433553a25d10608433.png);
  animation: spin 4s linear infinite;
}

.icon-logo-r-95,
.light-theme .icon-logo-r-95,
.dark-theme .icon-logo-r-95
{
  animation: wobble 2s infinite;
  background-image: url(https://i.gifer.com/RPKT.gif);
}
