@-moz-document domain("youtube.com") {
/* раздел: " ГЛАВНАЯ " /  Главная страница */

/* ####  Количество Видео в Сетке на Главной странице  #### */

/*сетка на 6 миниатюр Главной страницы - modern Chrome*/
[style] * {
    
    --ytd-rich-grid-items-per-row: 6 !important; /*6;*/ /* Количество Видео в ряд на главной странице.*/
    --ytd-rich-grid-posts-per-row: 4; /*4;*/
    --ytd-rich-grid-movies-per-row: 8; /*8;*/
}

/**************************************************************************************/
/* размер Миниатюр Видео на главной страницы - дефолт 89%  */
            /* 93%  после обновления и отцентровки в феврале 2021 */            
#contents.ytd-rich-grid-renderer, #grid-title.ytd-rich-grid-renderer {width: 93%;}

/**************************************************************************************/

        /*сетка на 6 миниатюр Главной страницы для старых браузеров*/
/* ## ВАЖНО: при изменении размера окна браузера, обновите страницу чтобы миниатюры ПРАВИЛЬНО просчитались заново ## */
.ytd-rich-item-renderer-2 {width: calc( 100% / 6 - 17px - 0.01px );}
.ytd-rich-item-renderer-3 {width: calc( 100% / 6 - 17px - 0.01px );}
/* =========== исправление положения иконок в Главном меню для старых браузеров ==============*/
.ytd-mini-guide-entry-renderer-1 .guide-icon.ytd-mini-guide-entry-renderer {
    margin-bottom: 6px;
    margin-left: 14px;}
.ytd-mini-guide-entry-renderer-1 .yt-simple-endpoint.ytd-mini-guide-entry-renderer {
    margin-bottom: 0px;
    margin-left: 12px;}
/*============================================================================================*/

/* ™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™™ */

/*   раздел: " ГЛАВНАЯ "   */


/* Отключить Аватары каналов на Главной странице v2 - NEW */
#avatar-link.ytd-rich-grid-media {height: 36px; margin-top: 12px; margin-right: 12px; display: none;}

/* Метаблок (НАЗВАНИЯ Видео) - Видимость текста - отступ справа*/
#meta.ytd-rich-grid-media {overflow-x: hidden;
    padding-right: 0px;} /*24px;*/ /* Видимость текста - отступ справа */

/* Весь Метаблок под Видео на Главной Странице - позиционирование */
#details.ytd-rich-grid-media {position: relative; cursor: pointer;
margin-top: 0px; -o-margin-right: 12px; margin-bottom: 0px; margin-left: -5px;
display: flex; flex-direction: row;}

/* Контур Видео на Главной Странице */
ytd-thumbnail.ytd-rich-grid-media:before {box-shadow: 0px 0px 1px 0px rgb(85, 102, 136);}


/**************************************************************/

/******* « перенесено в раздел » "Миниатюры" *******/

        /*расстояние между Видео на главной странице - Макси Мод*/

        /*расстояние между Видео на главной странице - Мини Мод*/

        /* Отступы для решётки Миниатюр Спраа и слева */

/******* « перенесено в раздел » "Миниатюры" *******/


/*========== + ===========*/
/* Главная*/
/* цвет блока, затенение(рамка), позиционирование */
#contents.ytd-rich-grid-renderer {background: var(--yt-spec-brand-background-primary);
    box-shadow: 0px 0px 1px 1px var(--ytd-searchbox-legacy-button-border-color);
    margin-top: 10px;
    margin-left: 2px; /*2px*/
    padding: 32px 0px 0px 0px; /*32px 0px 0px 9px;*/
    -o-position: relative; bottom: 0px; left: 0px;}

/* Центровка ВИДЕО на ГЛАВНОЙ странице - NEW */
ytd-rich-grid-media {
    --yt-button-compact-background-color: var(--yt-spec-10-percent-layer);
    --yt-button-compact-text-color: var(--yt-spec-text-secondary);
    width: 100%;
    margin-top: 0px;
    margin-right: -9px;
    margin-bottom: 0px;
    margin-left: 0px;
    display: block;
    max-width: var(--ytd-rich-grid-item-max-width);}

/* Центровка ВИДЕО на ГЛАВНОЙ странице (старый вариант, неработает послео бновления Ютуба) */
ytd-thumbnail.ytd-rich-grid-video-renderer {
    width: 100%;
    position: relative; /*relative;*/
    margin-left: 5.5px;}


/* Центровка Названий ВИДЕО на ГЛАВНОЙ странице + Ращзмер Шрифта - NEW */
#video-title.ytd-rich-grid-media {
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: normal;
    letter-spacing: normal; /*normal*/
    margin-left: 5px;
    -o-margin-right: -8px;                 /*test*/
    margin-top: -7px;
    font-size: 1.3rem;/*1.4rem*/
    font-weight: 500;
    line-height: 1.6rem;
    max-height: 3.2rem;}
/* Названия ВИДЕО на ГЛАВНОЙ странице - Ращзмер Шрифта в уменьшенном состоянии Миниатюр - NEW */
ytd-rich-grid-media[mini-mode] #video-title.ytd-rich-grid-media {
    font-size: 1.1rem; /* 1.1rem */
