    /* Тело Плеера Эпизодов */
#contents.ytd-macro-markers-list-renderer {
    overflow-y: auto; display: flex; flex-direction: column;
    background-color: #222222; box-shadow: 0px 0px 0px 2px var(--yt-searchbox-background  );
    -o-outline: 1px solid var(--yt-spec-themed-blue); 
    margin: 2px 2px 2px 2px; padding: 4px 2px 4px 2px;}

    /* Эпизоды (все кроме Активного) */
ytd-macro-markers-list-item-renderer {
    -o-display: block; padding: 8px 8px 8px 0; display: flex; flex-direction: row; flex: none; 
    background-color: #222222; box-shadow: inset 0px -1px 0px 0px rgba(4, 4, 4, .36);}

    /* Эпизод (Активный)*/
ytd-macro-markers-list-item-renderer[active] {
    background: rgba(255, 255, 255, .1);/* var(--yt-spec-badge-chip-background) */
    box-shadow: inset -1px 0px 0 0px #6f6f6f,
    inset 1px 0px 0 0 #6f6f6f, inset 0 0px 0 1.0px #6f6f6f;}

    /* Обводка Миниатюры активного Эпизода*/
