    outline: 2px solid #3ea6ff;} /* outline: 2px solid var(--yt-spec-themed-blue)*/

    /* Подложка под Миниатюру активного Эпизода */
#thumbnail.ytd-macro-markers-list-item-renderer {height: 56px; width: 100px;background: #000;}

    /* Подсвеченный Эпизод */
ytd-macro-markers-list-item-renderer:hover {
    background-color: transparent; 
    box-shadow: inset 0 0 10px 0 #3ea6ff, inset 1.3px 0 0 0 #3ea6ff, 
    inset -1.3px 0 0 0 #3ea6ff, inset -1px 0 0 0 #3ea6ff, inset 0 0 0 1px #3ea6ff;
    -o-box-shadow: inset 0px 0px 5px 2px rgba(0, 0, 0, .91);}

    /* Нажатый Эпизод */
ytd-macro-markers-list-item-renderer:active {background-color: #252a3a;}

    /* Точка Выделитель проигрываемого Эпизода */
#active-indicator.ytd-macro-markers-list-item-renderer {
    background: #3ea6ff; /* var(--yt-spec-themed-blue)*/
    height: 6px; width: 6px; border-radius: 6px; margin: 0 5px;}

    /* Названия Эпизодов */
h4.ytd-macro-markers-list-item-renderer {
    margin-bottom: 8px;
    color: #CACACA; /* var(--yt-spec-text-primary) */ /*Цвет Шрифта*/
    display: -webkit-box; /* так и должно быть, обычного нет */
    max-height: 3.2rem; /*calc(2 * var(--yt-link-line-height, 1.6rem))*/
    overflow: hidden; text-overflow: ellipsis; white-space: normal;
    font-size: var(--yt-link-font-size, 1.3rem); /*def. 1.4rem*/
    font-weight: 500;
    line-height: var(--ytd-link-line-height, 1.6rem);
    letter-spacing: var(--yt-link-letter-spacing, normal);}

    /* ТаймКод Эпизода */
#time.ytd-macro-markers-list-item-renderer {
    padding: 2px 6px; border-radius: 2px;
    color: #3ea6ff; /*Цвет времени*/    /* var(--yt-spec-themed-blue)*/
    background-color: #252a3a; /*Цвет поля времени*//* var(--yt-spec-suggested-action)*/
    font-size: 1.3rem; font-weight: 500; letter-spacing: 0.007px;
    text-transform: uppercase; align-self: flex-start;}

    /* Контейнер Синхронизации */
#sync-container.ytd-macro-markers-list-renderer {
    position: absolute; left: 0; right: 0; bottom: 0;
    height: 60px; -o-width: auto; -o-width: 417px;
    margin-right: 15px;
    transition: -webkit-transform 0.2s cubic-bezier(0.05, 0, 0, 1);
    transition: transform 0.2s cubic-bezier(0.05, 0, 0, 1);
    transition: transform 0.2s cubic-bezier(0.05, 0, 0, 1), -webkit-transform 0.2s cubic-bezier(0.05, 0, 0, 1);
    display: flex; flex-direction: row; align-items: center;
    justify-content: center; -o-background-color: var(--yt-spec-brand-background-primary);/*фон для теста*/}

    /* Кнопка Синхронизации */
#sync-button.ytd-macro-markers-list-renderer {
    color: #030303; /*цвет Шрифта кнопки*/ /* var(--yt-spec-text-primary-inverse) */
