    padding: 8px 12px; border-radius: 28px;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.3), 0 0 4px rgba(0, 0, 0, 0.2);
    cursor: pointer; font-size: var(--yt-badge-font-size, 1.2rem);
    font-weight: 500; line-height: var(--yt-badge-line-height-size, 1.2rem);
    margin-bottom: 6px; /*test*/}

/* Размер Шрифта Оглавления */
#title.ytd-engagement-panel-title-header-renderer {font-size: 1.4rem;}

/* Шапка Оглавления */
#header.ytd-engagement-panel-title-header-renderer {
