    background-color: var(--yt-spec-brand-background-primary); /*этот цвет: rgba(33, 33, 33, .98) */
    display: flex; align-items: flex-start;}

/*Эпизоды - размер иконки Поделиться*/
ytd-macro-markers-list-item-renderer.ytd-macro-markers-list-renderer 
