@-moz-document domain("youtube.com") {
/*   меню (Поделиться)   */

/* Позицианирование Кнопок Промотки в меню (Поделиться) */
#scroll-button-forward.yt-third-party-share-target-section-renderer  /* Правая */
{right: -16px;} /*def. -14px*/
#scroll-button-back.yt-third-party-share-target-section-renderer     /* Левая */
{left: -16px;} /*def. -14px*/

/* Кнопка (Поделиться - Активная) */
.scroll-button.yt-third-party-share-target-section-renderer:hover {
    margin-left: -14px;
    margin-right: -14px;
    width: 36px; /*40px;*/
    height: 60px; /*40px;*/
    top: 6px;
    border-radius: 10%; /*50%;*/
    background-color: var(--yt-spec-brand-background-primary);
    color: var(--yt-spec-icon-inactive);
    overflow: hidden;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.3), 0 0 4px rgba(0, 0, 0, 0.2),
    inset 0px 0px 1px 1px rgba(227, 227, 227, .1);}

/* Кнопка (Поделиться) */
.scroll-button.yt-third-party-share-target-section-renderer {
    margin-left: 2px;
    margin-right: 2px;
    position: absolute;
    width: 20px; /*40px;*/
    height: 60px; /*40px;*/
    top: 6px; 
    border-radius: 10%; 
    opacity: 1;
    background-color: var(--yt-spec-brand-background-primary);
    color: var(--yt-spec-icon-inactive);
    overflow: hidden;
    box-shadow: 0 4px 4px rgba(0, 0, 0, 0.3), 0 0 4px rgba(0, 0, 0, 0.2), 
    inset 0px 0px 1px 1px rgba(227, 227, 227, .1);}

/* Стрелки Кнопки (Поделиться) - Позицианирование */
.scroll-button-icon.yt-third-party-share-target-section-renderer {
    width: 24px; height: 24px; margin: 0 0px 0 -2.0px;}
}

@-moz-document domain("youtube.com") {
/*   Нотификационное Меню Колокольчика + Выпадающие Меню   */

/* Выпадающие Меню ЛайвЧата : - Внешний Контур и Тень - 2021 */
ytd-menu-popup-renderer.yt-live-chat-app 
{box-shadow: 0px 0px 1.5px 0px rgb(0, 0, 0), 0 3px 3px rgba(0, 0, 0, 0.3) !important;}

/* Выпадающие Меню (Все) : - Внутренний Контур (уже хз) */
paper-listbox {
    display: block;
    padding: 8px 0;
    background: var(--paper-listbox-background-color, var(--primary-background-color));
    box-shadow: inset 0px 0px 0px 1px var(--yt-spec-10-percent-layer);
    -o-border-radius: 3px;
    -o-border: 1px solid #f00; /* для теста Скроллбаров */
    color: var(--paper-listbox-color, var(--primary-text-color));
    overflow: hidden;}

/* Внешний Контур и Тень на множестве Выпадающих меню - 2021 */
#contentWrapper > .ytd-popup-container.style-scope {
    box-shadow: 0px 0px 1.5px 0px rgb(0, 0, 0), 0 3px 3px rgba(0, 0, 0, 0.3) !important;}

/* Внутренний Контур на множестве Выпадающих меню - 2021 */
tp-yt-paper-listbox.ytd-menu-popup-renderer {
    box-shadow: inset 0px 0px 0px 1px var(--yt-spec-10-percent-layer) !important; border-radius: 3px !important;}

/* Внутренний контур Меню: - Сохранить в / + Новый плейлист / Конфиденциальность - 2021 */
tp-yt-paper-listbox#entries.dropdown-content.ytd-dropdown-renderer {box-shadow: inset 0px 0px 0px 1px var(--yt-spec-10-percent-layer);}



/* Выпадающее Меню (Упорядочить) : - Внешний Контур, Тень,  Внутренний Контур - 2021 */
div.dropdown-content.tp-yt-paper-menu-button {
    box-shadow: 0px 0px 0px 0.3px rgb(0, 0, 0), 0 4px 4px rgba(0, 0, 0, 0.3)!important;}
/* Выпадающее Меню (Упорядочить) : - Внутренний Контур - 2021 */
tp-yt-paper-listbox.yt-dropdown-menu {
    box-shadow: inset 0px 0px 0px 1px var(--yt-spec-10-percent-layer) !important;}

/* Выпадающее Меню (Упорядочить) : - Внешний Контур, Тень,  Внутренний Контур(X)(уже хз) */
.dropdown-content.paper-menu-button {
    -o-box-shadow: var(--shadow-elevation-2dp_-_box-shadow);
    -o-border: 1px solid var(--yt-spec-10-percent-layer);
    box-shadow: 0px 0px 0px 0.3px rgb(0, 0, 0), 0 4px 4px rgba(0, 0, 0, 0.3);
    position: relative;
    border-radius: 3px;
    background-color: var(--paper-menu-button-dropdown-background, var(--primary-background-color));}

/* Контур вокруг Меню Колокольчика и Меню Пользователя */
iron-dropdown.ytd-popup-container {box-shadow: 0px 0px 0.4px 0.3px rgb(0, 0, 0), 0 4px 8px rgba(0, 0, 0, 0.3);}


/* Нотификационное Меню Колокольчика (выделенное) */
ytd-multi-page-menu-renderer[menu-style=multi-page-menu-style-type-notifications] > .menu-container.ytd-multi-page-menu-renderer 
{overflow: auto !important; margin-right: 0px; -o-background: rgb(251, 8, 8) !important;}
/* Нотификационное Меню Колокольчика (НЕ выделенное) */ 
/* /!\ overflow: auto !important - показать скроллбар ; margin-right: 0px !important - отцентровать окно со скроллбаром /!\ */
ytd-multi-page-menu-renderer[menu-style=multi-page-menu-style-type-notifications] > .menu-container.ytd-multi-page-menu-renderer:not(:hover)::-webkit-scrollbar-thumb
{overflow: auto !important; margin-right: 0px !important; 
    -o-background: rgb(251, 8, 8) !important; 
    background: var(--yt-spec-icon-disabled) !important;}
/* толщина СкроллБара в Колокольчике */
.ytd-multi-page-menu-renderer.menu-container::-webkit-scrollbar {width: 8px !important;   height: 8px !important;}
/* Нотификационное Меню Колокольчика (НЕ выделенное) - показывать СкроллБар ВЕЗДЕ */ 
-o-.ytd-multi-page-menu-renderer.menu-container::-webkit-scrollbar-thumb:not(:hover)
{overflow: visible !important; margin-right: 0px !important; background: var(--yt-spec-icon-disabled) !important;}


/* Секция в меню Нотификаций Колокольчика */
ytd-notification-renderer {
    position: relative;
    padding: 16px 8px 16px 1px; /*16px 16px 16px 0;*/
    display: flex;
    flex-direction: row;
    background-color: rgba(55, 55, 55, .05);
    box-shadow: inset 0px -0.8px 0px 0px var(--yt-spec-10-percent-layer);}

/* Секция в меню Нотификаций Колокольчика (Выделенная) */
ytd-notification-renderer:hover {
    position: relative;
    padding: 16px 8px 16px 1px; /*16px 16px 16px 0;*/
    display: flex;
    flex-direction: row;  
    background-color: rgba(132, 126, 120, .2) !important;
    box-shadow: inset 0 -1px 0 0 var(--yt-spec-10-percent-layer),
    inset 0 -1px 0 0 var(--yt-spec-10-percent-layer),
    0 -1px 0 0 var(--yt-spec-10-percent-layer),
    0 -0.4px 0 0 var(--yt-spec-10-percent-layer),
    inset -0.5px 0 0 0 var(--yt-spec-10-percent-layer),
    inset 1px 0 0 0 var(--yt-spec-10-percent-layer);}

/*Отступы внутри Секции одной Нотификации  v1 */
a.yt-simple-endpoint.ytd-notification-renderer {
    margin-right: 24px;
    padding: 2px 0px 0px 0px;
    flex: 1;
    flex-basis: 1e-9px;
    display: flex;
    -o-background: rgb(251, 8, 8) !important;
    flex-direction: row;}

/*Отступы внутри Секции одной Нотификации  v2 */
.yt-simple-endpoint.ytd-notification-renderer {
    min-width: 0;
    display: inline-block;
    cursor: pointer;
    text-decoration: none;
    -o-background: rgb(251, 8, 8) !important;
    color: var(--yt-endpoint-color, var(--yt-spec-text-primary));
    -o-padding: 2px 0px 0px 0px;}

/* Отступы в Заголовках (Недавние Комментарии) и ( Другие Уведомления) в  Меню Колокольчика */
h2.yt-multi-page-menu-section-renderer {
    padding-bottom: 4px; /* test */
    color: var(--yt-primary-text-color);
    margin: var(--ytd-margin-2x) 0px var(--ytd-margin-base) var(--ytd-margin-4x); 
    /* Def: margin: var(--ytd-margin-4x) 0px var(--ytd-margin-base) var(--ytd-margin-4x)*/
    font-weight: 400;
    flex: var(--layout-flex_-_flex);
    flex-basis: var(--layout-flex_-_flex-basis);}

/* Толщина СкроллБаров  в Меню  Колокольчика */
.menu-container.ytd-multi-page-menu-renderer::-webkit-scrollbar {width: 8px;   height: 8px;} /*Def. 8px*/
}

@-moz-document domain("youtube.com") {
/*  ДОПОЛНИТЕЛЬНЫЕ НАСТРОЙКИ оформления: при просмотре YouTube не заходя на Аккаунт  */

/* Мини Кнопки [Подписаться] под каналами - "не заходя на Аккаунт" (позицианирование) */
ytd-button-renderer.ytd-grid-channel-renderer.style-compact-gray.size-default {
    position: relative; top: -15px; left: 41px; margin: 0px 0px 0px 0px; padding: 0px 0px 0px 0px; 
    border-radius: 2px; box-shadow: 0px 0px 0px 1px rgba(150, 150, 150, .5);}

/* Мини Кнопки [Подписаться] под каналами - "не заходя на Аккаунт" (шрифт, размер, позицианирование) */
yt-formatted-string#text.ytd-button-renderer.style-compact-gray.size-default {
    font-size: 1.1rem; text-transform: none; margin: -3px 0px -2px 0px; padding: 1px 0px 0px 0px ;}

/* Мини Кнопки [Подписаться] под ИЗБРАННЫМИ каналами на странице Канала - "не заходя на Аккаунт" (позицианирование)*/
ytd-button-renderer.ytd-mini-channel-renderer.style-compact-gray.size-default {
    position: relative; top: -15px; left: 41px;
    box-shadow: 0px 0px 0px 1px rgba(150, 150, 150, .5);}

/* Размер Большой кнопки [Подписаться] по высоте, по ширине - "не заходя на Аккаунт" + (размер шрифта) */
tp-yt-paper-button#button.ytd-button-renderer.style-destructive.size-default {
    border-radius: 2px; box-shadow: 0px 0px 0px 1px rgba(150, 150, 150, .5); padding: 4.0px 10px; font-size: 1.4rem;}

/* Позицианирование Большой кнопки [Подписаться] на каналах - "не заходя на Аккаунт" */
    ytd-button-renderer.ytd-c4-tabbed-header-renderer.style-destructive.size-default {margin-right: 4px;}

/* Позицианирование Большой кнопки [Подписатьсяв] в Поиске - "не заходя на Аккаунт"  */
    ytd-button-renderer.ytd-channel-renderer.style-destructive.size-default {margin-right: 4px;}

/* Позицианирование Большой кнопки [Подписатьсяв] под Видео - "не заходя на Аккаунт"  */
    ytd-button-renderer.ytd-video-secondary-info-renderer.style-destructive.size-default {margin-right: 4px;}

/* Верхний бордюр для Правого меню (…) рядом с кнопкой [ВОЙТИ] */
ytd-multi-page-menu-renderer.ytd-popup-container {box-shadow: inset 0px 1px 0px 0px var(--yt-spec-10-percent-layer);}
}

@-moz-document domain("youtube.com") {
/*   Главное ЛЕВОЕ Меню (Гид Меню)   */

/* интервалы по высоте между строками - Левого Главного меню (Гид меню) */
ytd-guide-section-renderer {
    --paper-item-min-height: 28px;} /*Funny. 32px*/ /*old. 28px*/


/* Размер Системных Иконов в Гид меню и отступ справа */
.guide-icon.ytd-guide-entry-renderer {
    --iron-icon-height: 24px; /*def. 24px*/   /*old. 18px ~ 21*/
    --iron-icon-width: 24px;  /*def. 24px*/   /*old. 18px ~ 21*/
    margin-right: 11px;       /*def. 24px*/   /*old. 10px ~ 14*/
    margin-left: -1px;       /* добавил для ровности, по умолчанию не было */
    -o-color: var(--yt-spec-brand-icon-inactive);}

/* Размер Иконок Подписок в Гид меню и отступ справа */
yt-img-shadow.ytd-guide-entry-renderer {
    height: 21px;           /*def. 24px*/   /*old. 20px ~ 22*/
    width: 21px;            /*def. 24px*/   /*old. 20px ~ 22*/
    margin-right: 13px;     /*def. 24px*/   /*old. 8px ~ 13*/
    background-color: transparent;
    overflow: hidden;}

/* Ширина Гид меню */
ytd-guide-renderer.ytd-app {
    width: 240px;}                 /*def. 240px*/ /*old. 228px*/


/* расположение Скроллбара на Гид меню и ширина Гид меню */
ytd-app {
    --app-drawer-width: 240px;}    /*def. 240px*/ /*old. 228px*/

/* бордюр-контур справа Гид меню */
-o-app-drawer {box-shadow: 0.5px 0px 0px 0px rgba(178, 178, 178, .18);} /*old.*/

/* отступы по бокам Секций Гид меню */
paper-item.ytd-guide-entry-renderer {
    padding: 0 20px 0 28px; /*def. 0 24px 0 24px;*/ /*old. 0 18px 0 32px;*/
    min-width: 0;
    height: var(--paper-item-min-height, 48px);} /*48px*/

/* Размер Шрифтов в Пунктах Гид меню */
.title.ytd-guide-entry-renderer {
    color: var(--yt-spec-text-primary);
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    font-size: 1.3rem; /*var(--ytd-user-comment_-_font-size)*/ /*old. 1.3rem*/
    font-weight: var(--ytd-user-comment_-_font-weight);
    line-height: var(--ytd-user-comment_-_line-height);
    letter-spacing: var(--ytd-user-comment_-_letter-spacing);
    flex: 1;
    flex-basis: 1e-9px;}

/* Размер Шрифтов в Пунктах Гид меню (активный раздел) */
ytd-guide-entry-renderer[active] .title.ytd-guide-entry-renderer {
    font-size: 1.3rem; /*def. 1.4rem*/ /*old. 1.3rem*/
    font-weight: 500;
    line-height: var(--yt-guide-highlight-line-height, 1.8rem);}


/* Отступ для Свайпа Гид Меню */
#contentContainer.tp-yt-app-drawer[swipe-open].tp-yt-app-drawer::after {
    position: fixed;    -o-background-color: rgb(255, 0, 0);
    top: 0; bottom: 0; left: 100%; visibility: visible; width: 5.8px;/*def. 20px*/ content: '';}
/* Отступ для Свайпа Гид Меню - подсветка */
#contentContainer.tp-yt-app-drawer[swipe-open].tp-yt-app-drawer:hover::after{
    background-color: rgba(174, 174, 174, .56); -o-width: 5px; /*def. 20px*/ content: '';}
[dark] #contentContainer.tp-yt-app-drawer[swipe-open].tp-yt-app-drawer:hover::after{
    background-color: rgba(255, 255, 255, .26);-o-width: 5px; /*def. 20px*/ content: '';}
/* Отступ для Свайпа Гид Меню - Скрыть подсветку в развёрнутом виде */
#contentContainer.tp-yt-app-drawer[opened].tp-yt-app-drawer:hover::after{
    background-color: #00bce100 !important; -o-width: 0px; /*def. 20px*/ content: '';}
}

@-moz-document domain("youtube.com") {
/*   Автоскрываемое Мини Гид меню  */

/* OFF */
        /* Граница срабатывания - вход */
ytd-mini-guide-renderer:after {
    transition: all 100ms ease-in-out 0.4s;
    border-radius: 0% 15% 0% 0% / 0% 30% 0% 0% /*0% 40% 0% 0% / 0% 30% 0% 0%*/;
    z-index: -1/*-1*/; display: inline-block; content: ""; box-shadow: 1px 0 0 0  rgb(255, 0, 0); -o-background: rgba(255, 0, 0, .11);
    position: absolute; top: 0; left: 0px; width:100%; height: 100%; padding-left: 27px /*46px*/; opacity: 0;}
/* OFF */
 ytd-mini-guide-renderer.ytd-app {
    transition: margin-left 100ms ease-in-out 0.4s;
    margin-left: -65px /* -66px */; 
    box-shadow: 1px 0px 0px 0px rgb(215, 215, 215); 
