@-moz-document domain("youtube.com") {
/* Сдвиг Видимой области Ютуба (связано с убираемым Мини Гид меню) 2021 */
ytd-app[mini-guide-visible_] ytd-page-manager.ytd-app {
    -o-background: rgba(236, 114, 114, .27);
    z-index: -100000000 !important;
    opacity: 1;
    margin-left: 8px/*33px*/; -o-padding-right: 22px; -o-width: calc(100% - 61px) !important;} /*def: 72px*/

/* тестовый общий сдвиг - в работе */
ytd-page-manager > *.ytd-page-manager:not(ytd-watch-flexy):not([page-subtype=history]):not([page-subtype=playlist]):not([guide-persistent-and-visible]):not(ytd-search):not([page-subtype=WEB_PAGE_TYPE_SETTINGS])
{background: var(--yt-spec-general-background-b , #F1F1F1) !important;
    -o-left: -24px !important;
    margin-left: 0px/*-20*/;}

/* Смещение Активной Области Каналов с Видимым Мини Гид меню */
[mini-guide-visible_] ytd-page-manager > *.ytd-page-manager [page-subtype=channels] 
{margin-right: -0.5px !important/*-1px*/; margin-left: 0.5px/*1px*/;}

/* Настройки смещения * ВЕЗДЕ * - с убранным Мини Гид Меню - V */
ytd-browse:not([mini-guide-visible]):not([guide-persistent-and-visible]):not([page-subtype=WEB_PAGE_TYPE_SETTINGS])
{margin-left: 0px !important;}

/* Настройки смещения Библиотеки(Истории) - ленты - V */
[mini-guide-visible_] [page-subtype=history] #primary.ytd-two-column-browse-results-renderer {-o-padding-left: 10px;/*20px*/margin-right: -9px/*-10px*/ !important; margin-left: 9px/*10px*/ !important;}
/* Old ver. */
-o-[mini-guide-visible_] [page-subtype=history] {-o-padding-left: 10px;/*20px*/margin-right: -10px !important}

/* Библиотеки(Истории) @media (min-width: 876px) - В Две колонки (Правая) */
 ytd-two-column-browse-results-renderer[page-subtype=history][has-secondary-column-data] #secondary.ytd-two-column-browse-results-renderer {
    -o-margin-left: 104px !important; margin-right: 0px !important; z-index: 300;}
/* Библиотеки(Истории) @media (min-width: 876px) - В Одну колонку (Верхняя) */
ytd-two-column-browse-results-renderer[page-subtype=history] #secondary.ytd-two-column-browse-results-renderer {
    padding-right: 0% /*def. 442px*/; margin-left: 0px !important; margin-right: 0px !important;}




/* Отступы для ТЕКСТА Справа - При открытом Гид Меню - для ВСЕХ видео в Вертикальных столбцах (включая в Тренде) !!!  - V */
