/* задержки при переключении пунктов меню */
#selectionBar.tp-yt-paper-tabs {transition: all 1ms linear 0s !important;}

/* МЕНЮ КАНАЛА: Главная, Видео, Плейлисты, Каналы, Обсуждение, О Канале (Дополнительные Параметры) */
/* новое 2021 */
tp-yt-paper-tabs.ytd-c4-tabbed-header-renderer {--paper-tabs-selection-bar-color: #f00;
--paper-tabs-selection-bar_-_border-bottom-width: 3px; box-sizing: border-box;
color: var(--yt-lightsource-secondary-title-color); height: 48px; padding-bottom: 1px; margin-left: 51px;}
#tabsContent.tp-yt-paper-tabs > :not(#selectionBar) {height: 48px !important;}
/*старое*/
paper-tabs.ytd-c4-tabbed-header-renderer {--paper-tabs-selection-bar-color: #f00; /* Цвет подчёркивания */
--paper-tabs-selection-bar_-_border-bottom-width: 3px; /* высота подчёркивания пункта меню*/
color: var(--yt-lightsource-secondary-title-color);  /* Цвет НЕ выделенных пунктов меню*/ height: 48px ;padding-bottom: 1px;}


/*Скрытие элементов прокрутки МЕНЮ КАНАЛА*/
paper-icon-button.tp-yt-paper-tabs {-o-visibility: hidden; opacity: 0;} /* новое 2021 */
paper-tabs.ytd-c4-tabbed-header-renderer paper-icon-button.paper-tabs {-o-visibility: hidden; opacity: 0;}
/*Скрытие элементов прокрутки МЕНЮ КАНАЛА - отображать скрытые (если они есть) при наведении курсора */
paper-icon-button.tp-yt-paper-tabs:hover {-o-visibility: hidden; opacity: 0.8;} /* новое 2021 */
paper-tabs.ytd-c4-tabbed-header-renderer paper-icon-button.paper-tabs:hover {-o-visibility: hidden; opacity: 0.5;}


/*------------------------------------------------------*/

/* Мини Аватары ( Видео с Канала ) на Главной странице Канала - ПОЗИЦИОНИРОВАНИЕ и по отношению к тексту */
#avatar.ytd-shelf-renderer {margin-left: 6px; height: 32px; width: 32px; margin-right: 2px; 
border-radius: 0%;background-color: transparent;overflow: hidden;}

/*Горизонтальная сетка главной страницы канала - отступ между видео - Сверху*/
#grid-container.ytd-expanded-shelf-contents-renderer > *.ytd-expanded-shelf-contents-renderer:not(:last-child) {margin-bottom: 6px; /*13px, второй вариант 6px*/}

/*Кнопки промотки горизонтальных списков видео*/
.arrow.yt-horizontal-list-renderer {width: 32px; height: 55px; border-radius: 24px; margin-top: 10px;  
box-shadow: 0 4px 4px rgba(0, 0, 0, 0.3), 0 0 4px rgba(0, 0, 0, 0.2), inset 0px 0px 1px 1px rgba(227, 227, 227, .1);}


/*(Кнопки промотки горизонтальных списков видео) : - Исправление косяка youTube нерабочей половины левой кнопки*/
#contentContainer.app-drawer[swipe-open].app-drawer::after {position: fixed; top: 0; bottom: 0; left: 100%; visibility: visible; width: 7px;}

/*Вертикальный СТОЛБЕЦ главной страницы канала + в Тренде (50% будет показывать два столбца Видео в блоке*/
/* (min-width : минимальный размер окна браузера в "px" при котором два столбца превратятся в один) */
/* по умолчанию min-width: 534px; - больше ставить нельзя, иначе прекратит срабатывать вид в два столбца на некоторых Каналах */
#grid-container.ytd-expanded-shelf-contents-renderer > *.ytd-expanded-shelf-contents-renderer {width: 50%; flex: auto; min-width: 534px; overflow: hidden;}

/*Текст названия Видео "Превью канала" и названия Видео в Вертикальном столбце канала + Позицианирование (Действует и на Тренды)*/
#video-title.ytd-video-renderer {font-size: 1.4rem; margin-top: 0px;}

/*текст Описания "Превью канала" и описания видео в Вертикальном столбце канала*/
#description-text.ytd-video-renderer 
{padding-top: 2px; -o-margin-bottom: 8px; max-height: 3.3rem; font-size: 1.2rem; font-weight: 500; padding-right: 3px; overflow-wrap: anywhere;}
/*отступ текста от правого края превью*/
ytd-video-renderer:not([use-prominent-thumbs]) ytd-thumbnail.ytd-video-renderer {margin-right: 11px;}

/*отступы Описаний Вертикальных Видео (преобразованных в столбцы - Треды и тд.) */
ytd-video-renderer.ytd-expanded-shelf-contents-renderer {padding-right: 8px; /*24px*/ box-sizing: border-box;}

/* Размер "Превью канала", и оно же Видео в "Трендах" + Контур */
ytd-video-renderer:not([use-prominent-thumbs]) ytd-thumbnail.ytd-video-renderer {
flex: none; width: 246px; height: 138px; box-shadow: 0px 0px 1px 0px rgb(85, 102, 136);}


/*===================================================================================================*/


/*размер превью видео в вертикальном блоке - X*/
-o-ytd-video-renderer:not([use-prominent-thumbs]) -o-ytd-thumbnail.ytd-video-renderer {
    -o-flex: none;
    -o-width: 246px;
    -o-height: 138px;
    margin-right: 11px;} /*отступ текста от правого края превью*/

/*******************/
}

