/* размер кнопки [Подписаться] по высоте, по ширине - 2021 добавил !important -  без этого глючило */
tp-yt-paper-button.ytd-subscribe-button-renderer {
    padding: 4.0px 10px !important;} /*padding: 6px 10px*/

/* размер кнопки [Спонсировать] по высоте, по ширине*/
ytd-button-renderer.style-suggestive[is-paper-button] tp-yt-paper-button.ytd-button-renderer {
    padding: 4px 1.5px !important;} /*padding: 4px 1.5px*/

#avatar.ytd-channel-renderer {width: 100px;} /*100px*/

/*не показывать Аватары каналов на Главной странице YouTube*/
#avatar-link.ytd-rich-grid-video-renderer {display: none;}

/* !!!!!!!!    размер Миниатюр главной страницы - дефолт 89% (моё 94%)    !!!!!!!!!!!! */
/*    width: 89%; */
-o-#contents.ytd-rich-grid-renderer, #grid-title.ytd-rich-grid-renderer {
    width: 89%;} /*width: 89%*/

/* скорее всего это были интервалы и смещение Миниатюр на главной странице, но видимо уже не работает */
#chips-below.ytd-rich-grid-renderer {
    width: 88%; /*88%*/
    margin-top: -2px;} /*-2px*/

/* ширина блока горизонтальных списков видео внутри Плейлиста*/
-o-ytd-playlist-sidebar-renderer {
    width: 320px;} /*320px , - YouTube def. 417px */

/*===================================================*/

/* ширина блока горизонтальных списков видео внутри Плейлиста - def. YouTube */
-o-ytd-playlist-sidebar-renderer {
    display: block;
    width: 417px;
    padding-top: 24px;
    padding-right: 32px;
    padding-bottom: 24px;
    padding-left: 32px;
    box-sizing: border-box;
    background-color: var(--yt-spec-general-background-a);
    overflow: auto;
    -o-min-height: 100px;}

/*===================================================*/

