css:
/* i really want this to be global */


body {
    background-color: #393939 !important;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) .l_contents:before {
    background-color: #393939;
    background-image: none;
}
.navbar-brand a {
    background-color: #4f4f4f;
}
.p_itemlist--box .service_name {
    color: #b3b3b3;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) header {
    height: 60px;
    border-top: 3px solid #393939;
    background: #393939;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) .swiper-container-bnr_box {
    background: black;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) .l_contents:after {
    background-image: none;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) .l_contents .l_contents_left {
    background: #6f0000;
}
.bg-light {
    background-color: #656565 !important;
}
html.cs_pc.is_show_pc_layout:not(.is_mobile_ua) .pc_footer {
    margin-top: 20px;
    background-color: #393939;
}
.horizon_center a {
    color: #ababab;
}
.bg-white {
    background-color: #393939 !important;
}
.table-success, .table-success > th, .table-success > td {
    background-color: #5b5b5b;
}
.card {
    background-color: #393939;
}
.card-header {
    background-color: #505050 !important;
}
.btn-link {
    color: #8c8c8c;
}

#serviceNameText {
 	color: #a1a1a1;   
}
.l_html5app .row_description {
    background-color: #484848;
    padding: 35px 0
}
.dot_bg, .l_html5app #controllBox {
    background: none;
}
footer#wrap_footer {
    margin-top: 0;
    border-top: 1px solid #ccc;
    background-color: #393939;
}
#footer #img_flow {
    padding-bottom: 10px;
    background-color: black;
    opacity: 0;
}
.prize_name {
 	color: #a1a1a1;   
}
.item_list_scroll, .l_html5app #js_carousel-inner {
    -webkit-flex-wrap: nowrap;
    -ms-flex-wrap: nowrap;
    -webkit-box-orient: horizontal;
    -webkit-box-direction: normal;
    -webkit-flex-direction: row;
    -ms-flex-direction: row;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    display: -webkit-box;
    flex-direction: row;
    flex-wrap: nowrap;
    padding: 4px 0;
    overflow-x: auto;
}
div#header div#header_main, div#header div#header_sub {
    border-top: 0 solid #ff4571;
    /*border-bottom: 5px solid #ff4571;*/
    background-color: #393939;
    box-shadow: 0 5px 10px #2F2F2F;
}
#nav_account>ul>li>a:not(.btn), #nav_global>ul>li>a:not(.btn), #nav_language>ul>li>a:not(.btn), #nav_mypage>ul>li>a:not(.btn) {
    color: #429cea;
}
.btn-group-xs>.btn, .btn-xs {
    color: #a6a6a6;
}
