/* ==UserStyle==
@name           Brick Hill+
@namespace      USO Archive
@author         arm
@description    `Made to resemble the scrapped site design.`
@version        20210630.0.10
@license        NO-REDISTRIBUTION
@preprocessor   uso
==/UserStyle== */

@-moz-document url-prefix("https://www.brick-hill.com/") {
.card .top:not(.absolute).blue,
nav div.primary {
    color: #fff;
    background: linear-gradient(0deg, #007CDC 0%, #00A9FE 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute),
.thread-label.blue {
    color: #fff;
    background: linear-gradient(0deg, #2584cc 0%, #47bbf5 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

nav .info {
    color: #fff;
    background: linear-gradient(0deg, #267ab900 0%, #4abef900 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).green {
    color: #fff;
    background: linear-gradient(0deg, #289420 0%, #18c40a 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).red,
.button:not(.plain):not([disabled]).red,
button:not(.plain):not([disabled]).red {
    color: #fff;
    background: linear-gradient(0deg, #b00 0%, #fe0000 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}


.card .top:not(.absolute).orange {
    color: #fff;
    background: linear-gradient(0deg, #e48e1b 0%, #ffb651 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.notif,
.nav-notif,
.alert.error {
    color: #fff;
    background: linear-gradient(0deg, #b00 0%, #fe0000 100%);
    font-weight: 600;
    border: 0;
}

nav div.secondary,
footer {
    color: #fff;
    background: linear-gradient(0deg, #252525 0%, #3a3a3a 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

textarea {
    border-radius: 3px;
}













.tabs .tab {
    border-radius: 0px 0px 0px 0px;
}



nav div ul li {
    padding-right: 3px
}
}

@-moz-document url-prefix("https://www.brick-hill.com/") {
.button.blue,
button.blue,
button:not(.plain):not([disabled]).blue {
    color: #fff;
    background: linear-gradient(0deg, #007CDC 0%, #00A9FE 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.green,
.button:not(.plain):not([disabled]).bucks,
.button:not(.plain):not([disabled]).green,
button:not(.plain):not([disabled]).bucks {
    color: #fff;
    background: linear-gradient(0deg, #289420 0%, #18c40a 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.orange,
button:not(.plain):not([disabled]).bits,
.button:not(.plain):not([disabled]).orange {
    color: #fff;
    background: linear-gradient(0deg, #c07c20 0%, #f39d25 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

button.small {
    color: #fff;
    background: linear-gradient(0deg, #9a9a9a 0%, #cbcbcb 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0;
}

.profile-card.award img {
    border: 0px;
}



.crate-types .active,
.crate-types :hover {
    border-left: 0px;
    background-color: #C3CDD8;
    transition: color .1s linear;
}
}
