css:
@-moz-document url-prefix("https://www.brick-hill.com/") {
.card .top:not(.absolute).blue {
    color: #fff;
    background: linear-gradient(0deg, #2f567b 0%, #437899 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute),
.thread-label.blue {
    color: #fff;
    background: linear-gradient(0deg, #2f567b 0%, #437899 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

nav .info {
    color: #fff;
    background: linear-gradient(0deg, #267ab900 0%, #4abef900 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).green,
.thread-label.green {
    color: #fff;
    background: linear-gradient(0deg, #199956 0%, #2fbb72 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.card .top:not(.absolute).red,
.button:not(.plain):not([disabled]).red,
button:not(.plain):not([disabled]).red,
.thread-label.red {
    color: #fff;
    background: linear-gradient(0deg, #9d0b1b 0%, #aa2230 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}


.card .top:not(.absolute).orange,
.thread-label.orange {
    color: #fff;
    background: linear-gradient(0deg, #ca862c 0%, #d7a053 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

.notif,
.nav-notif,
.alert.error {
    color: #fff;
    background: linear-gradient(0deg, #9d0b1b 0%, #aa2230 100%);
    font-weight: 600;
    border: 0;
}


nav div.secondary,
footer {
    color: #fff;
    background: linear-gradient(0deg, #242424 0%, #262626 100%);
    border-radius: 0px;
    font-weight: 600;
    border: 0;
}

nav div.primary {
    background: linear-gradient(0deg, #262c35 0%, #353c46 100%);
}

textarea {
    border-radius: 3px;
}













.tabs .tab {
    border-radius: 0px 0px 0px 0px;
}



nav div ul li {
    padding-right: 3px
}
}

@-moz-document url-prefix("https://www.brick-hill.com/") {
.button.blue,
button.blue,
button:not(.plain):not([disabled]).blue,
.input-group .input-button {
    color: #fff;
    background: linear-gradient(0deg, #2f567b 0%, #437899 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0px;
}

button.green,
.button:not(.plain):not([disabled]).bucks,
.button:not(.plain):not([disabled]).green,
button:not(.plain):not([disabled]).bucks,
button:not(.plain):not([disabled]).green,
.button:not(.plain):not([disabled]).green {
    color: #fff;
    background: linear-gradient(0deg, #199956 0%, #2fbb72 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0px;
}

button.orange,
button:not(.plain):not([disabled]).bits,
.button:not(.plain):not([disabled]).orange {
    color: #fff;
    background: linear-gradient(0deg, #ca862c 0%, #d7a053 100%);
    border-radius: 2px;
    font-weight: 600;
    border: 0px;
}

.button:not(.plain) {
    color: #fff;
    background: linear-gradient(0deg, #9a9a9a 0%, #cbcbcb 100%);
    border-radius: 0px;
    border: 0px;
    font-weight: 600;
}

.profile-card.award img {
    border: 0px;
}



.crate-types .active,
.crate-types :hover {
    border-left: 0px;
    background-color: #3f4244;
    transition: color .1s linear;
}
}
