css:
@-moz-document domain("amazon.co.jp") {
table[id*="tabular"] *
{
    line-height : 1em !important;
    overflow : visible !important;
    position : inherit !Important;
    max-width : 100% !important;
    max-height : 100% !important;
    margin : 0px !important;
    padding : 0px !important;
    z-index : 0 !important;
    opacity : 1 !important;
    height : 100% !important;
    width : 100% !important;
}
table[id*="tabular"]  tr,
#merchant-info a
{
    border : 2px dotted silver;
    border-radius : 5px;
    padding : 8px 5px !important;
    margin : 5px 0 !important;
    display : block !important;
}

table[id*="tabular"] td,
table[id*="tabular"] .a-truncate
{
    display : block !important;
}
table[id*="tabular"] tr td:nth-child(1) *
{
    font-size : 8px !important;
    color : gray !important;
}
table[id*="tabular"] .a-truncate-cut
{
    display : none !important;
}
table[id*="tabular"] .a-truncate-full,
#merchant-info a
{
    font-size : 1.7em !important;
    padding : 6px 5px 3px !important;
    line-height : 1.2em !important;
    text-shadow : 2px 2px 3px lightgray !important;
    letter-spacing : -0.5px !Important;
}

table[id*="tabular"] .a-truncate-full
{
    color : dimgray;
}
table[id*="tabular"] a[href*="/gp/help/customer"],
#merchant-info a[href*="/gp/help/customer"]
{
    color : deepskyblue;
    font-weight : bolder !Important;
    letter-spacing : 0px !Important;
}
#sellerProfileTriggerId,
table[id*="tabular"] a[href*="/gp/help/seller"],
#merchant-info a[href*="/gp/help/seller"]
{
    color : #ff0018e0 !important;
    text-decoration : underline !Important;
    line-height : 1.3em !important;
}
#merchant-info a
{
    text-align : center !important;
    padding : 13px 9px !important;
    background-color : #fafafa;
}
}
