css:
@-moz-document url-prefix("https://www.chiphell.com/thread"), url-prefix("https://www.chiphell.com/forum.php?mod=viewthread&tid=") {
div.sign {
    display:none;
}
div.pls dl.cl {
    display:none;
}
div.pls p.md_ctrl {
    display:none;
}
table.plhin > tbody > tr:nth-child(2) > td {
    display:none;
}
table.plhin > tbody > tr.ad {
    display:none;
}
table.plhin > tbody > tr:nth-child(4) > td.plc {
    position:relative;
}
td.pls > div.pls > p:nth-child(5) {
    display:none;
}
td.pls > div.pls > p:nth-child(6) {
    display:none;
}
ul.xl.xl2.o.cl {
    display:none;
}
div#postlist > div {
    border:1px;
    border-color: lightgray;
    border-style: solid;
    border-bottom: 1px;
}
tbody > tr:nth-child(4) > td.plc > div > div.pob {
    float:right;
    padding-right:10px;
}
.pob {
    line-height: auto;
}
.pr, .pc {
    margin:0;
}
.po {
    border-top:0px;
}
.po .y {
    margin: 6px 0 0 5px;
}
}

@-moz-document url-prefix("https://www.chiphell.com/forum-") {
th.new > a:visited{
	color: #ccc !important;
}
}
