css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {

#p-Mitmachen, #p-navigation, #p-interaction, #p-logo, #siteNotice {
    display: none !important;
}


#p-lang {
    position: absolute !important;
    top: -170px !important;
}

#p-lang > div.body > ul > li{
  display: none;
  padding-left: 20px !important;
  padding-top: 2px !important;
  background-repeat: no-repeat !important;
  background-position: top left !important;
}

#p-lang > div.body > ul > li.interwiki-de {

    display: list-item  !important;
    background-image: url(http://www.clickfire.com/images/flags/germany.gif) !important;
}

#p-lang > div.body > ul > li.interwiki-en {
    display: list-item !important;
    background-image: url(http://www.clickfire.com/images/flags/u-k.gif) !important;
}


#p-lang > div.body > ul > li.interwiki-fr {
    display: list-item !important;
    background-image: url(http://www.clickfire.com/images/flags/france.gif) !important;
}

#p-lang > div.body > ul > li.interwiki-es {
    display: list-item !important;
    background-image: url(http://www.clickfire.com/images/flags/spain.gif) !important;
}

#p-lang > div.body > ul > li.interwiki-it {
    display: list-item !important;
    background-image: url(http://www.clickfire.com/images/flags/italy.gif) !important;
}


}
