body {
    background: transparent !important;
}
.content {
    background: transparent !important;
}
#fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url(https://i.pinimg.com/originals/42/da/4d/42da4d9f286a664d49f955265d75a77e.gif) !important;
    /* Background */
    background-size: cover !important;
    background-attachment: fixed !important;
}
#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
    background: linear-gradient(to bottom, #f7b0c5, #F7F7F7);
}
#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4) {
    display: none;
}
#GamesPageLeftColumn {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
}
#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}
#navigation {
    background-image: url(https://i.gifer.com/embedded/download/4M5E.gif);
    background-color: #edca9d;
}
.notification-red {
    background-color: #f7b0c5;
    padding: 1px 6px;
    min-width: 18px;
}
.notification-blue {
    background-color: #f7b0c5;
    padding: 1px 6px;
    min-width: 18px;
}
.rbx-btn-primary-lg {
    background-color: rgba(144, 238, 144);
}
.rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus {
    background-color: rgba(255, 255, 255);
}
.avatar-back {
    background-image: url(https://i.pinimg.com/originals/cf/03/f6/cf03f6209045b202d1918a9cec64c337.gif);
    background-color: #FFFFFF;
}
.game-card-container {
    background-color: #f7b0c5;
    position: relative;
    border-radius: 3px;
    float: left;
    height: 100%;
    margin: 0 auto;
    padding: 0;
    text-align: left;
    width: 100%;
    border-color: transparent transparent transparent #ecebec;
}
#GamesListsContainer > div {
    background-color: transparent !important;
    width: 100% !important;
}
.section-content {
    background-color: #debcb8;
    padding: 15px;
    position: relative;
}
@keyframes rainbow {
    0% {
        background-color: #edca9d;
        border-color: #edca9d;
    }
    14% {
        background-color: #d1a771;
        border-color: #d1a771;
    }
    28% {
        background-color: #ffbb5e;
        border-color: #ffbb5e;
    }
    42% {
        background-color: #f77d36;
        border-color: #f77d36;
    }
    100% {
        background-color: #edca9d;
        border-color: #edca9d;
    }
}
.btn-common-play-game-lg {
    animation: rainbow 10s infinite;
}
.icon-logo-r, .light-theme .icon-logo-r, .dark-theme .icon-logo-r {
    background-image: url(https://1.bp.blogspot.com/-uxXB2-Nyzc0/XJGKSyjOfAI/AAAAAAAyQWE/LDBFeqtMqg0OlmKh22G3qMFwWJi_urD-ACLcBGAs/s1600/AW3744403_03.gif);
}
