css:
@-moz-document domain("schulgestaltungswiki.de") {h1,
h2 {
    border-bottom: 0px solid #a2a9b1;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #008037;
}

.toc {
    display: table;
    padding: 10px 20px;
    border: 0px;
    background-color: #efeeee;
}

.mw-editsection {
    display: none;
}

#mw-page-base {
    background-color: #efeeee;
    background-image: none;
}
.mw-body {
    border-color: #ffffff;
}
body {
    background-color: #efeeee;
}

#mw-head {
    background: #efeeee;
}

.toc {
    margin: auto;
    display: block;
    padding: 10px 20px;
    border: 0px;
    background-color: #efeeee;
    max-width: 66%;
}

.catlinks {
    border: 0px solid #a2a9b1;
    background-color: #efeeee;
    padding: 7px 10px;
}

.portal h3,
.vector-menu-portal h3 {
    font-weight: bold;
    background-image: none;
    color: #008037;
}

a {
    color: #0055c5;
}

.vector-menu-tabs {
    background-image: none !important;
    background-color: #fff !important;
}

.vector-menu-tabs li {
    line-height: 0.75;
}

.vector-menu-tabs li a {
    background-color: #fff;
    background-image: none;
    border: 1px;
    border-color: #efeeee
}

.vector-menu-tabs .selected {
    background: #ffffff
}

.vector-menu-tabs, .vector-menu-tabs a, #mw-head .vector-menu-dropdown h3{
    background: #efeeee;
    background-image: none;
}


.vector-menu-tabs .selected a,
.vector-menu-tabs .selected a:visited {
    color: #202122;
    text-decoration: none
}

.vector-menu-tabs .mw-watchlink.icon a{
    line-height: 1;
}

.vector-menu-dropdown {
line-height: 0.75;
}

.vector-menu-dropdown .vector-menu-content-list{
    background: #fff;
    border-top: 1px #efeeee;
    left: 0px;
    border: 0px;
    box-shadow: 0px 0px 7px rgba(0,0,0, 0.2);
    clip-path: inset(0px -5px -5px -5px);
    line-height: 3em;
}

#p-search form {
    margin: 0.3em 0 0;
    
}

#searchInput {
    background: #fff;
    border: 0px;
    padding: 0.6em 1.84615385em 0.6em 0.6em;
}


.vector-menu-dropdown h3:after {
    top: 0;
}

.c-avatar img {
    padding: 0px;
    border: 0px solid #dcdcdc;
    background-color: #fff;
}

.comments-body {
    font-size: 1em;
}

.c-item {
    padding: 0px;
    margin: 0px;
}

.c-item.reply {
    background: #efeeee !important;
    margin-bottom: 0px;
}
}
