css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("know.namics.com") {
/* Ausrichtung Homepage */
.tabletitle { width:auto !important; }
#flickr_badge_uber_wrapper {margin-top:-5px !important;padding:15px 15px 15px 15px !important; background-color:rgb(242, 242, 242) !important; width:100% !important;}
#flickr_badge_wrapper {border:none !important;}
.flickr_badge_image {padding-top:15px !important; padding-left:15px !important; padding-bottom:10px !important;}
#flickr_badge_image9 {padding-right:15px !important;}
a[href="http://www.flickr.com/groups/namics/"] {display:none !important;}
.rssMacro {margin-left:15px !important;}
.rssMacro table {margin:0 !important; }

/* Abstand unten */
.yui-g .wiki-content {padding-bottom:20px !important;}

/* Flex-Layout News */
.news-panel {width:49.85% !important;}
.news-panel .date, .news-panel .blogpost {width:inherit !important;float:none !important;line-height:1.5em;}
.news-panel .user-pic {margin-right:10px !important;}
.news-panel .news-links li a {font-weight:normal !important;}

/* BlogPosts Korrektur */
.blogpost .wiki-content img {max-width: 640px !important;}
.blogpost .attachments {}
.blogpost td, .blogpost th {white-space:normal !important; font-size: 75% !important;line-height:1.2em !important;}
.blogpost td:first-child, .blogpost th:first-child, .blogpost td:last-child, .blogpost th:last-child {display:none !important;}
.wiki-content .blogpost {padding-left:20px !important;padding-right:20px !important;}

.comment-body li {font-size:12px !important; }

/* Links unterstreichen */
.wiki-content p a, .wiki-content li a, .confluenceTable a  {padding-bottom:0px !important;}
.heatmap li a, .tableview a, .recentlyUpdatedItem a, .grid a, .people-profile-box a, #space-services a {border-bottom:none !important; text-decoration:none !important;}

/* Search */
#gsa-result input[type=text] {width:600px;}
.gsa-td-f {padding-bottom:10px !important;}
#gsa-result .td-select {vertical-align:top !important;}
#gsa-result input[type=submit] {background:transparent url(http://know.namics.com/s/1418/89/1.4/_/download/resources/com.namics.confluence.plugins.namics-classic-theme:theme-resources/images/buttons_bg.gif) no-repeat scroll 0 0;color:#fff !important;text-transform:capitalize;font-weight:bold !important;border-top:none !important;border-left:none !important;padding:3px 10px !important;}

/* Workmarke */
#wordmarkspage {background:transparent !important;line-height:1em !important;padding: 0px 8px !important;}
#wordmarkspage em {color:#771518 !important;}
#ez {text-align:left !important; border: 1px solid !important; border-color: #D2CED0 #999 #999 #D2CED0 !important; background-color:#fff !important;left:auto !important;top:auto !important; padding: 0px !important; width:250px;}
#ez li {padding:.25em !important;}
#ez textarea {background-color:#fff !important;border-color:#fff !important;}
}
