css:
@-moz-document domain("jeuxvideo.com"), domain("www.forumjv.com"), domain("open.spotify.com"), domain("embed.spotify.com") {

/* fonds généralistes */
.layout--withBackground,#jv-footer,#jv-footer .highlight {
    background-color: #1B2631;
    color: #D3D3D3;
}
/* HEADER : MENU  */
 .jv-header-menu .jv-header-top {
    background-color: #283747;
}
.jv-header-menu .jv-nav-open {
    background-color: #283747;
}
/* texte menu */
 .jv-header-menu .jv-nav-open .jv-nav-open-text {
    color: orange;
}
/* texte survolé */
 .jv-header-menu .jv-nav-open:active .jv-nav-open-text, .jv-header-menu .jv-nav-open:hover .jv-nav-open-text {
    color: white;
}
/* fonds du menu */
 .jv-header-menu .jv-nav-open.on {
    background-color: #1B2631;
}
.jv-header-menu .jv-nav-open .jv-nav-open-hamburger {
    background-color: #283747;
}
/* couleur croix du menu */
 .jv-header-menu .jv-nav-open .jv-nav-open-hamburger:after, .jv-header-menu .jv-nav-open .jv-nav-open-hamburger:before {
    background: darkorange;
}
.jv-header-menu .jv-nav-open.on .jv-nav-open-hamburger:after, .jv-header-menu .jv-nav-open.on .jv-nav-open-hamburger:before {
    background: darkorange;
}
.jv-header-menu .jv-nav-open.on:active .jv-nav-open-hamburger:after, .jv-header-menu .jv-nav-open.on:active .jv-nav-open-hamburger:before, .jv-header-menu .jv-nav-open.on:hover .jv-nav-open-hamburger:after, .jv-header-menu .jv-nav-open.on:hover .jv-nav-open-hamburger:before, .jv-header-menu .jv-nav-open:active .jv-nav-open-hamburger {
    background: darkorange;
}
/* items du menu */
 .jv-header-menu .jv-nav-open.on .jv-nav-open-hamburger {
    background: #1B2631;
}
.jv-header-menu .jv-nav-primary, .jv-header-menu .jv-main-nav.on:after {
    background-color: #283747;
}
.jv-header-menu .jv-nav-platform {
    background-color: #1B4F72;
}
.jv-header-menu .jv-header-bottom .jv-nav-lvl1-item>.nav-link {
    color: #D3D3D3;
}
.jv-header-menu .jv-header-bottom .jv-nav-primary .live-indicator {
    background-color:transparent;
}
.jv-header-menu .jv-nav-primary .jv-nav-lvl1-item>.nav-link:hover {
    background-color:transparent;
    color:white;
}
.jv-header-menu .jv-nav-lvl1-item .nav-link:hover {
    background-color:transparent;
}
.jv-header-menu .jv-nav-platform .jv-nav-lvl1-item>.nav-link:hover {
    color:orange;
}
.jv-header-menu .jv-nav-toggler-on~.jv-nav-lvl2 {
    background-color: #1B4F72;
}
.jv-header-menu .jv-nav-lvl2-item>.nav-link {
    color: #D3D3D3;
}
.jv-header-menu .jv-main-nav.jv-nav-lvl2-on .jv-nav-lvl1>.jv-item-lvl2-on>.jv-nav-toggler {
    background-color: #880E4F;
}
.jv-header-menu .jv-header-bottom .jv-nav-primary .jv-nav-toggler:hover {
    background-color: #880E4F;
}
.jv-header-menu .jv-header-bottom .jv-nav-primary .live-indicator {
    background-color: #FF5722;
}
.sponsoTab .colorThemeG {
    background-color: #2874A6;
}
.jv-header-menu .jv-nav-lvl2-item>.nav-lv2-link-repeat {
    background-color: #880E4F;
}
/* bordures */
 .jv-header-menu .jv-nav-platform .jv-nav-lvl1-item, .jv-header-menu .jv-nav-primary .jv-nav-lvl1-item, .jv-header-menu .jv-nav-primary .jv-nav-lvl2-item {
    border-bottom: none;
}
.jv-header-menu .jv-nav-primary .jv-nav-lvl1-item:first-child {
    border-top: none;
}
/* FOND DES ARTICLES */
 .layout--withBackground .layout__breadcrumb, .layout--withBackground .layout__content, .layout--withBackground .layout__contentAfter, .layout--withBackground .layout__contentAside, .layout--withBackground .layout__contentBefore, .layout--withBackground .layout__contentHeader, .layout--withBackground .layout__contentMain, .layout--withBackground .layout__contentMainMedia, .layout--withBackground .layout__contentMainMediaContainer, .layout--withBackground .layout__contentTop, .layout--withBackground .layout__contextBottom {
    background-color: #283747;
}
.video-footer-container {
    background-color: #1B2631;
}
.highlight {
    background-color: #1B2631;
}
.sideModule {
    background-color: #1B4F72;
}
.jv-header-menu .jv-header-bottom .jv-nav-lvl1-item>.nav-link {
    background-color: transparent;
}
/* texte a lire aussi */
 .sideModule {
    color:#D3D3D3;
}
/* NEWS */
 .container-welcome-home {
    background-color: transparent;
}
.quick-access-bar {
    background-color: #880E4F;
}
.quick-access-bar .title {
    background-color: #1B4F72;
}
.quick-access-bar .link, .quick-access-bar .title, .quick-access-bar .title {
    color : #D3D3D3;
}
.item-home-accroche .foot-accroche {
    background-color: #1B4F72;
}
.item-home-accroche .group-wrapper .titre-wrapper a {
    color : #D3D3D3;
}
.item-home-accroche .mtag {
    background-color: #1B2631;
    color : #D3D3D3;
}
.accr-item-large .foot-accr-item-large, .item-small-pres {
    background-color: #1B4F72;
}
.accr-item-large .mtag {
    background-color: #1B2631;
    color : #D3D3D3;
}
.item-small-pres .a-titre-item .content-type {
    color:#FF5722;
}
.bloc-actu-home ul.list-actu-home .titre-actu, .bloc-actu-home ul.list-actu-home .type-actu {
    color:#D3D3D3;
}

.container-row-video{
	background-color: #283747;
}

.container-row-video .accr-item-large .foot-accr-item-large, .container-row-video .item-small-pres{
	background-color: #1B4F72;
}

.bloc-onfire.container-row-lists{
	background-color: #283747;
}

.bloc-onfire .list-onfire-link{
	background-color: #1B4F72;
}

.bloc-onfire .menu-onfire,.bloc-onfire .list-onfire-link, .item-small-pres .a-titre-item,.accr-item-large .a-titre-item{
	color: #B3B3B3;
}


/* INDEX FORUM */
 .forum-section {
    background-color: #880E4F;
}
.fs-body a {
    color : #D3D3D3;
}
.fs-body h3:first-child {
    background: none;
}
.content-slice-pres1 {
    background-color: #1B4F72;
}
.content-slice-pres1.top-forums a {
    color : #D3D3D3;
}
#form-recherche .search input, .bloc-recherche .search input, .forum-search .search input {
    background-color: #1B2631;
}
.panel-jv-forum {
    background-color: #1B4F72;
}
.panel-jv-forum .titre-info-fofo {
    color : #D3D3D3;
}
.panel-jv-forum .bloc-forums-preferes .lien-jv, .panel-jv-forum .bloc-info-forum .lien-jv, .panel-jv-forum .bloc-sous-forums .lien-jv {
    color : #D3D3D3;
}
.sideOrderedGames__gameContainer {
    background-color: #1B2631;
}
.sideOrderedGames__gameTitle {
    color : #D3D3D3;
}
/* FORUM */
 .titre-bloc {
    color : #D3D3D3;
}
.jv-header-menu #jv-header-search {
    background-color: #1B4F72;
}
.jv-header-menu #jv-header-search .search input {
    color : #D3D3D3;
}
.jv-header-menu #jv-header-search .search>.autocomplete-jv {
    background-color: #1B2631;
}
.jv-header-menu #jv-header-search .search>.autocomplete-jv li, .jv-header-menu .account .account-pseudo {
    color : #D3D3D3;
}
.jv-header-menu .nav-link-account, .jv-header-menu .nav-link-search, .jv-header-menu .jv-header-top .nav-link-account, .jv-header-menu .jv-header-top .nav-link-search,.jv-header-menu .account-pseudo {
    color: #FF5722;
}

.jv-header-menu .jv-nav-dropdown-container-top, .jv-header-menu .jv-nav-dropdown-container-content, .jv-header-menu .account:first-of-type, .jv-header-menu .jv-nav-dropdown-container-bottom {
    background-color: #1B4F72;
}
.jv-header-menu .jv-nav-dropdown-item-label {
    color: #D3D3D3;
}
.jv-header-menu .jv-nav-dropdown-author {
    color: white;
}
.jv-header-menu .jv-account-number-mp, .jv-header-menu .jv-account-number-notif {
    color: #FF5722;
}
.topic-list li:nth-of-type(2n), .topic-list li.dfp__atf~li:nth-of-type(odd) {
    background-color: #283747;
}
.topic-list li:nth-of-type(odd), .topic-list li.dfp__atf~li:nth-of-type(2n) {
    background-color: #46556D;
}
.topic-list {
    background : none;
    border : none;
}
.topic-head {
    background-color: #1B2631;
}
.jv-editor .conteneur-editor {
    background-color: #1B4F72;
}
.lien-jv, a {
    color: #D3D3D3;
}
.jv-editor .area-editor {
    background-color: #1B2631;
}

.bloc-message-forum,.bloc-message-forum:nth-of-type(odd){
	/*border: none!important;*/
	border: 1px solid;
}

.jvchat-ruler{
	border-width: thin;
	border-style: none none solid none;
}

.topic-list .topic-head{
	 background: #46556D!important;
	 color: #D3D3D3;
}

/* MESSAGES */
 .bloc-message-forum, .bloc-message-forum .conteneur-message {
    background: #283747;
}
.bloc-message-forum .bloc-pseudo-msg {
    color: white;
}
.gameHeaderBanner, .gameHeaderSubNav {
    background: #1B4F72;
}

.jv-header-menu .jv-nav-dropdown-unread{
    background:  #880E4F;
}

.jv-editor .btn-group{
	background-color: #880E4F;
}

 #f-fstk{
	background-color: #283747;
}

.jv-editor .btn-jv-editor-toolbar{
	color: #D3D3D3;	
}

#f-fstk.f-jvc-thm .f-tabs-w, #f-fstk.f-jvc-thm .f-tabs-w .f-tabs-r, #f-fstk.f-jvc-thm .f-top-w .f-top{
	background-color: #1B4F72;
}

.form-control{
	background-color: #1B2631;
}

/* PAGE DES JEUX */

.layout{
    background-color: #1B2631;
}

.gameCharacteristicsMain__block, .bloc-presentation-affi, .bloc-critique, .gameAffiliate,.latestVideos,.latestNews, .userOpinions, .userOpinions__mostUsefulContainer, .latestImages,.gameCharacteristicsDetailed, .sideTips__body,.gameNewsList__card, .sideGameAffiliate__affiliateBlock .gameAffiliate__container, .gameReviewsList__card, .reviewConclusionBlock{
    background-color: #283747;
	color: #D3D3D3;
}

.gameCharacteristicsMain__releaseDate, .gameCharacteristicsMain__synopsis, .bloc-avis-lecteur .auteur, .bloc-avis-testeur .auteur, .gameAffiliate__tr .gameAffiliate__td,.latestVideos__header, .latestVideos__titleWrapper,.latestNews__titleLink, .gameHeaderBanner__title,.latestNews__header, .latestImages__header,.gameCharacteristicsDetailed__title,.gameCharacteristicsDetailed__td--th,.gameCharacteristicsDetailed__td,.sidePopularThreads__td:first-child .sidePopularThreads__link, .sideSimilarGames__gameLink, .sideTips__tipsLabelLink, .gameNewsList__titleLink, .gameHeaderSubNav__itemLink, .gameReviewsList__titleLink, .gameReviewsList__description,.hardware-side-module-header-title,.hardware-side-module-block-configuration,.hardware-side-module-information-title, .hardware-side-module-information-description, .bloc-avis .bloc-pseudo-avis, .form-post-topic .form-control,.pres-item-jaq .titre-item a{
    color: #D3D3D3;
}

.resume-jaquette .outils-jaquette, .hardware-side-module,.hardware-side-module-block, .hardware-side-module-information{
	background-color:#1B4F72;
}

.bloc-presentation-affi{
	border: none;	
}

.encart-option-article{
	background-color:#880E4F;
}

.userOpinions__opinionRating,.bloc-avis .note-avis strong{
	color: #FF5722;
}

.gameCharacteristicsDetailed__td--th,.sidePopularThreads__body,.sideSimilarGames__body{
	background-color: #1B2631;
}

.gameNewsList__metadata:after, .gameReviewsList__metadata:after{
	background: transparent;
}

.bloc-recap-avis, .accordeon-single-line .contenu-asl, .accordeon-single-line,.bloc-avis{
	background-color:#1B2631;
}

.bloc-pres-filtre{
	background-color:#1B4F72;
}

/*  #1B4F72 bleu clair
	#283747 bleu foncé
	#1B2631 bleu très foncé
